INSERT INTO `posts` (`id`, `name`, `summary`, `content`, `image`, `show`, `view`, `price`, `promotion_price`, `id_trademark`, `id_distributor`, `place`, `timetake`, `telephone`, `email`, `type`, `created_at`, `updated_at`) VALUES
(1, 'Cách chọn sơn nội thất cao cấp', 'Nếu như Sơn ngoại thất được ví như bộ đồ bên ngoài mà bạn mang để tôn lên nét đẹp và đường nét cơ thể bạn, thì Sơn nội thất được ví như nét đẹp tâm hồn của con người. Chính vì vậy, nếu bạn tô điểm cho ngôi nhà của mình  bằng những sắc màu nội thất tươi mới, điều đó chắc chắn sẽ đem lại cảm giác đầy phấn chấn và cả năng lượng mới mẻ nữa. Tuy nhiên, để việc điểm tô cho “tâm hồn” của mái ấm thân thương đạt kết quả mỹ mãn như ý, thì ngoài việc chọn lựa màu sắc phù hợp, bạn sẽ phải tìm kiếm cả những tính năng vượt trội và hữu ích khác của sản phẩm Sơn Nội Thất. Dưới đây là những bí quyết về cách chọn sơn nội thất cao cấp mà có thể rất hữu ích cho bạn. 1. Tính Thẩm Mỹ Cao: Màu sắc Sơn Nội thất và cách phối màu sơn', '<p><em><strong>1. T&iacute;nh Thẩm Mỹ Cao: M&agrave;u sắc Sơn Nội thất v&agrave; c&aacute;ch phối m&agrave;u sơn</strong></em></p>\r\n\r\n<p><img alt=\"\" src=\"https://www.mykolor.net.vn/image/data/son-noi-that-cho-phong-lam-viec.jpg\" /><br />\r\nM&agrave;u&nbsp;<strong>sơn nội thất&nbsp;</strong>đẹp sau khi ho&agrave;n thiện c&oacute; thể &ldquo;trẻ h&oacute;a&rdquo; v&agrave; mang lại sức sống cho m&aacute;i ấm của bạn. V&igrave; thế, m&agrave;u sơn m&agrave; bạn chọn kh&ocirc;ng những phải ph&ugrave; hợp với phong c&aacute;ch, phong thủy của bạn m&agrave; c&ograve;n phải thể hiện sự hiếu kh&aacute;ch cho căn nh&agrave;. M&agrave;ng&nbsp;<strong>sơn nội thất</strong>&nbsp;phải l&aacute;ng mịn đẹp kh&ocirc;ng tỳ vết v&agrave; c&oacute; vẻ b&oacute;ng mờ sang trọng. M&agrave;u sắc&nbsp;<strong>sơn nội thất</strong>&nbsp;giữ được vẻ tươi đẹp v&agrave; bền m&agrave;u theo thời gian. Khi ng&ocirc;i nh&agrave; y&ecirc;u dấu của bạn được kho&aacute;c một lớp &aacute;o mới ho&agrave;n hảo, chất lượng cuộc sống cũng như gi&aacute; trị của ng&ocirc;i nh&agrave; bạn sẽ được n&acirc;ng cao.<br />\r\n<strong>SƠN NƯỚC NỘI THẤT B&Oacute;NG SEMI &ndash; MYKOLOR</strong>&nbsp;TOUCH SEMIGLOSS FINISH FOR INTERIOR được cấu tạo bởi th&agrave;nh phần ch&iacute;nh l&agrave; keo Pure Acrylic.&nbsp;<em><strong>Đ&acirc;y l&agrave; loại sơn nước nội thất cao cấp, với 1040 m&agrave;u sắc đẹp. Bề mặt sơn ho&agrave;n thiện b&oacute;ng, mịn tạo cảm gi&aacute;c sơn chắc chắn. Độ phủ cao, dễ lau ch&ugrave;i v&agrave; chống nấm mốc th&iacute;ch hợp cho việc trang tr&iacute;, bảo vệ tường cho những c&ocirc;ng tr&igrave;nh cao cấp.</strong></em><br />\r\nĐặc điểm sơn nội thất Semi For Int<br />\r\n&bull;&nbsp;&nbsp; &nbsp; Ch&ugrave;i rửa được<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Bảo vệ 5 năm<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Độ b&aacute;m d&iacute;nh cao<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Độ che phủ cao<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Chống nấm mốc v&agrave; rong r&ecirc;u<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Bề mặt sơn mịn m&agrave;ng v&agrave; b&oacute;ng nhẹ.</p>\r\n\r\n<p>2. Khả Năng Ngăn Ngừa Vi Khuẩn, Chống Nấm Mốc Cao<br />\r\n<img alt=\"\" src=\"https://www.mykolor.net.vn/image/data/son-noi-that-cao-cap-mykolor.jpg\" />SƠN NỘI THẤT B&Oacute;NG KH&Aacute;NG KHUẨN 5 PLUS &ndash; MYKOLOR TOUCH 5 PLUS FINISH l&agrave; loại sơn d&ugrave;ng để pha m&agrave;u bằng m&aacute;y pha tự động, chuy&ecirc;n d&ugrave;ng để trang tr&iacute; cho c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng cao cấp, Sơn c&oacute; độ b&oacute;ng mờ, độ che phủ cao, cho m&agrave;u sắc đẹp phong ph&uacute; với bộ sưu tập 1040 m&agrave;u, sơn ch&ugrave;i rửa được, th&iacute;ch hợp cho việc trang tr&iacute; v&agrave; bảo quản bề mặt tường nội thất. Dễ sử dụng. ĐẶC BIỆT: Loại sơn n&agrave;y c&oacute; khả năng kh&aacute;ng 05 loại vi khuẩn thường gặp trong điều kiện b&igrave;nh thường: Escherichia Colibacteria (g&acirc;y bệnh đường ruột, tiết niệu), Staphylococcus Aurerus Bacteria (g&acirc;y bệnh ngo&agrave;i da, vi&ecirc;m phổi), Salmonella Typhimurium Bacteria (g&acirc;y bệnh ti&ecirc;u chảy), Bacillus Subtilis Bacteria (g&acirc;y bệnh rối loạn ti&ecirc;u ho&aacute;), Psuedomonas Aeruginosa (g&acirc;y bệnh vi&ecirc;m phổi, vi&ecirc;m đường h&ocirc; hấp)<br />\r\nĐặc t&iacute;nh:<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Kh&aacute;ng được 5 loại vi khuẩn<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Độ che phủ cao<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Mịn m&agrave;ng v&agrave; b&oacute;ng nhẹ<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Chống b&aacute;m bẩn<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Chống b&aacute;m bụi cao<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Ch&ugrave;i rửa được<br />\r\n3. T&iacute;nh Năng Chịu Ch&ugrave;i Rửa Tối Đa</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.mykolor.net.vn/image/data/son-noi-that-cao-cap-mykolor%202.jpg\" /><br />\r\n&ldquo;Trẻ h&oacute;a&rdquo; ng&ocirc;i nh&agrave; của bạn bằng những sắc m&agrave;u tươi mới cũng đồng nghĩa với việc bạn phải &ldquo;bảo vệ&rdquo; vẻ đẹp đ&oacute; trước những vết ố bẩn. Ng&agrave;y nay, với sơn nội thất cao cấp sở hữu t&iacute;nh năng ch&ugrave;i rửa tuyệt vời, tất cả bạn cần chỉ l&agrave; một chiếc khăn ẩm, hay ch&uacute;t x&agrave; ph&ograve;ng v&agrave; một chiếc b&agrave;n chải mềm, l&agrave; sự tinh tươm tức khắc được trả về nguy&ecirc;n vẹn m&agrave; kh&ocirc;ng hề ảnh hưởng đến m&agrave;ng sơn. M&agrave;ng sơn kh&ocirc;ng bị b&agrave;o m&ograve;n c&ograve;n m&agrave;u sắc th&igrave; vẫn vẹn nguy&ecirc;n như l&uacute;c ban đầu.<br />\r\n4. Th&acirc;n Thiện Với M&ocirc;i Trường<br />\r\n<img alt=\"\" src=\"https://www.mykolor.net.vn/image/data/son-noi-that-cao-cap-mykolor%203.jpg\" /><br />\r\nBạn n&ecirc;n chọn những sản phẩm th&acirc;n thiện với m&ocirc;i trường. Sơn nội thất cao cấp kh&ocirc;ng những th&acirc;n thiện với sức khỏe người ti&ecirc;u d&ugrave;ng m&agrave; c&ograve;n với m&ocirc;i trường bởi n&oacute; c&oacute; chỉ số VOC thấp (VOC l&agrave; hợp chất hữu cơ dễ bay hơi g&acirc;y ảnh hưởng kh&ocirc;ng tốt đến sức khỏe). Ngo&agrave;i ra sơn nội thất cao cấp cũng sẽ kh&ocirc;ng th&ecirc;m Ch&igrave; &amp; Thủy Ng&acirc;n. Th&ecirc;m v&agrave;o đ&oacute;, sơn nội thất cao cấp c&ograve;n đạt được chứng nhận th&acirc;n thiện m&ocirc;i trường do c&aacute;c tổ chức uy t&iacute;n tr&ecirc;n thế giới cấp v&iacute; dụ như chứng nhận &ldquo;Green Label&rdquo; của Ủy ban M&ocirc;i trường Singapore (SEC).<br />\r\n5. Đem Lại Vẻ Đẹp L&acirc;u Bền Cho Ng&ocirc;i Nh&agrave; Của Bạn</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.mykolor.net.vn/image/data/son-noi-that-cao-cap-mykolor%20(2).jpg\" /><br />\r\nGia đ&igrave;nh của bạn xứng đ&aacute;ng được tận hưởng cảm gi&aacute;c thoải m&aacute;i trong một ng&ocirc;i nh&agrave; thật đẹp với m&agrave;u sắc sang trọng, tinh tế, bền bỉ với thời gian. C&aacute;c bức tường kh&ocirc;ng n&ecirc;n bị bong tr&oacute;c, mất thẩm mỹ với những vết nứt nhỏ tr&ecirc;n bề mặt, c&ograve;n m&agrave;u sắc th&igrave; xỉn mờ biến đổi. Sơn nội thất tốt nhất sẽ kh&ocirc;ng để xảy ra điều đ&oacute;, bởi n&oacute; gi&uacute;p bảo vệ tường nh&agrave; bạn theo năm th&aacute;ng, giữ cho ng&ocirc;i nh&agrave; vẻ tuyệt đẹp ban đầu.</p>', '1627893148_3d094205626b9535cc7a(1).jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 08:32:28', '2021-08-02 08:32:28'),
(2, 'Cách chọn màu sơn nhà để hợp phong thuỷ với gia chủ', 'Cách chọn màu sơn nhà để hợp phong thuỷ với gia chủ: Theo sắp xếp của các nhà phong thủy thì màu sắc thuộc các hành như sau: màu đỏ thuộc hành Hỏa (phương Nam), màu xanh thuộc hành Mộc (phương Đông), màu xanh dương và xám thuộc hành Thủy (phương Bắc), màu trắng thuộc hành Kim (phương Tây), màu vàng thuộc hành Thổ (Trung tâm). Các bạn khi chọn màu sơn nhà thường chọn những màu thịnh hành hoặc theo ý thích của bản thân, điều đó có thể gây ra những điều không tốt trong phong thủy đấy. Bạn nên cân nhắc kĩ màu sơn hợp với mệnh và hướng nhà, có như vậy mới đảm bảo vận khí và nguồn năng lượng cần thiết. Để tìm hiểu thông tin màu sắc nhà ở mời các bạn tham khảo thông tin dưới đây của mecuben.com để biết thêm nhiều thông tin nhé.', '<h2>C&aacute;ch chọn m&agrave;u sơn nh&agrave; để hợp phong thuỷ với gia chủ</h2>\r\n\r\n<p>Theo sắp xếp của c&aacute;c nh&agrave; phong thủy th&igrave; m&agrave;u sắc thuộc c&aacute;c h&agrave;nh như sau: m&agrave;u đỏ thuộc h&agrave;nh Hỏa (phương Nam), m&agrave;u xanh thuộc h&agrave;nh Mộc (phương Đ&ocirc;ng), m&agrave;u xanh dương v&agrave; x&aacute;m thuộc h&agrave;nh Thủy (phương Bắc), m&agrave;u trắng thuộc h&agrave;nh Kim (phương T&acirc;y), m&agrave;u v&agrave;ng thuộc h&agrave;nh Thổ (Trung t&acirc;m).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; vậy, trong trang tr&iacute; nội thất, gia chủ n&ecirc;n d&ugrave;ng m&agrave;u bản mệnh hoặc m&agrave;u tương sinh. Với m&agrave;u tương sinh, những người mệnh Kim n&ecirc;n sử dụng m&agrave;u v&agrave;ng, người mệnh Thổ hợp với m&agrave;u hồng hoặc đỏ, người mệnh Thủy n&ecirc;n d&ugrave;ng nhiều m&agrave;u trắng, người mệnh Mộc m&agrave;u đen v&agrave; mệnh Hỏa m&agrave;u xanh.</p>\r\n\r\n<h2>Chọn m&agrave;u sơn tường nh&agrave; ph&ugrave; hợp?</h2>\r\n\r\n<p>Khi lựa chọn m&agrave;u sơn cho nh&agrave; ở, nhiều gia chủ vẫn thường băn khoăn giữa việc n&ecirc;n chọn m&agrave;u sắc ph&ugrave; hợp với mệnh, theo hướng c&ocirc;ng tr&igrave;nh hay theo hướng kh&iacute; hậu. Điều đ&oacute; g&acirc;y kh&oacute; khăn cho việc sử dụng sơn m&agrave;u ph&ugrave; hợp cho ng&ocirc;i nh&agrave;. Tuy nhi&ecirc;n, mỗi c&aacute;ch lựa chọn c&oacute; một ưu điểm ri&ecirc;ng, sẽ gi&uacute;p kh&ocirc;ng gian sống h&agrave;i h&ograve;a, c&acirc;n bằng &aacute;nh s&aacute;ng v&agrave; tạo cảm gi&aacute;c dễ chịu cho c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh.</p>\r\n\r\n<ul>\r\n	<li>Chọn m&agrave;u sắc theo mệnh</li>\r\n	<li>Chọn m&agrave;u theo hướng c&ocirc;ng tr&igrave;nh</li>\r\n	<li>Chọn m&agrave;u theo hướng kh&iacute; hậu</li>\r\n</ul>\r\n\r\n<h3>1. C&aacute;ch lựa chọn m&agrave;u sắc sơn nh&agrave; theo mệnh của gia chủ:</h3>\r\n\r\n<ul>\r\n	<li>Mệnh Kim: N&ecirc;n sử dụng sơn m&agrave;u trắng hoặc sơn m&agrave;u v&agrave;ng.</li>\r\n	<li>Mệnh Mộc: Sơn m&agrave;u xanh hoặc m&agrave;u đen.</li>\r\n	<li>Mệnh Thủy: Sơn m&agrave;u đen, xanh lam, m&agrave;u trắng.</li>\r\n	<li>Mệnh Hỏa: Sơn m&agrave;u hồng, m&agrave;u dam cam, m&agrave;u đỏ.</li>\r\n	<li>Mệnh Thổ: Sơn m&agrave;u v&agrave;ng đất, m&agrave;u n&acirc;u, m&agrave;u v&agrave;ng x&aacute;m.</li>\r\n</ul>\r\n\r\n<p>Sở dĩ c&oacute; sự lựa chọn tr&ecirc;n l&agrave; do quy luật ngũ h&agrave;nh: Thổ sinh Kim, Kim -&gt; Thủy, Thủy -&gt; Mộc, Mộc -&gt; Hỏa, Hỏa -&gt; Thổ</p>\r\n\r\n<h3>2. C&aacute;ch chọn m&agrave;u sơn nh&agrave; theo hướng cửa ch&iacute;nh( Hướng của c&ocirc;ng tr&igrave;nh)</h3>\r\n\r\n<ul>\r\n	<li>Hướng Đ&ocirc;ng: sơn m&agrave;u xanh</li>\r\n	<li>Hướng T&acirc;y: sơn m&agrave;u trắng, sữa.</li>\r\n	<li>Hướng Nam: sơn m&agrave;u đỏ, hồng.</li>\r\n	<li>Hướng Bắc: sơn m&agrave;u ghi, x&aacute;m, xanh dương</li>\r\n</ul>\r\n\r\n<p><strong>Nh&agrave; hướng T&acirc;y, T&acirc;y Bắc</strong>&nbsp;đ&oacute;n nhiều &aacute;nh s&aacute;ng n&ecirc;n chọn c&aacute;c gam m&agrave;u lạnh v&agrave; m&agrave;u trung t&iacute;nh như xanh l&aacute;, xanh da trời, trắng sữa&hellip; Nh&agrave; hướng Bắc &iacute;t &aacute;nh s&aacute;ng, d&ugrave;ng m&agrave;u trắng s&aacute;ng gi&uacute;p kh&ocirc;ng gian s&aacute;ng sủa hơn. Nh&agrave; hướng Đ&ocirc;ng Bắc hợp với c&aacute;c gam m&agrave;u n&oacute;ng để l&agrave;m bớt sự lạnh lẽo.</p>\r\n\r\n<p><strong>Nh&agrave; hướng Nam, Đ&ocirc;ng Nam, T&acirc;y Nam</strong>&nbsp;c&oacute; nguồn s&aacute;ng ổn định, ấm &aacute;p n&ecirc;n hợp với bất kỳ m&agrave;u n&agrave;o m&agrave; chủ nh&acirc;n ưa th&iacute;ch. Đối với từng căn ph&ograve;ng, bạn cũng n&ecirc;n c&acirc;n nhắc c&aacute;c m&agrave;u sắc ph&ugrave; hợp. Ph&ograve;ng l&agrave;m việc hay ph&ograve;ng học th&iacute;ch hợp với c&aacute;c m&agrave;u đỏ, cam, v&agrave;ng. Đ&acirc;y l&agrave; những m&agrave;u truyền cảm hứng l&agrave;m việc, thuộc năng lượng dương. Ngược lại, c&aacute;c m&agrave;u thuộc năng lượng &acirc;m như xanh, trắng, hồng&hellip;. mang lại sự y&ecirc;n tĩnh v&agrave; những cảm x&uacute;c y&ecirc;u thương, ph&ugrave; hợp với ph&ograve;ng ngủ.</p>\r\n\r\n<h3><strong>3. C&aacute;ch chọn m&agrave;u sơn nh&agrave; theo kh&iacute; hậu:</strong></h3>\r\n\r\n<ul>\r\n	<li><strong>Nh&agrave; theo hướng ch&iacute;nh T&acirc;y, T&acirc;y Bắc</strong>, thời gian &aacute;nh nắng mặt trời chiếu trực tiếp v&agrave;o nh&agrave; nhiều nhất, ưu điểm l&agrave; tận dụng được tốt nguồn s&aacute;ng tự nhi&ecirc;n, tuy nhi&ecirc;n điểm hạn chế l&agrave; ch&uacute;ng g&acirc;y cảm gi&aacute;c n&oacute;ng nực, kh&oacute; chịu cho người sử dụng, đặc biệt l&agrave; v&agrave;o m&ugrave;a h&egrave;. Bởi vậy nh&agrave; hướng n&agrave;y n&ecirc;n n&ecirc;n chọn m&agrave;u lạnh, trung h&ograve;a như xanh da trời, l&aacute; c&acirc;y, trắng, sữa&hellip;.</li>\r\n	<li><strong>Nh&agrave; hướng Nam, Đ&ocirc;ng Nam v&agrave; T&acirc;y Nam</strong>&nbsp;l&agrave; những hướng tốt bởi c&oacute; nguồn s&aacute;ng ổn định, đ&oacute;n gi&oacute; tốt v&agrave; kh&ocirc;ng kh&iacute; ấm &aacute;p n&ecirc;n c&oacute; thể phối m&agrave;u hợp với từng kh&ocirc;ng gian sử dụng hoặc theo sở th&iacute;ch của chủ nh&acirc;n ng&ocirc;i nh&agrave;.</li>\r\n	<li><strong>M&agrave;u n&oacute;ng cho nh&agrave; hướng Đ&ocirc;ng Bắc:</strong>&nbsp;Nh&agrave; hướng Đ&ocirc;ng Bắc n&ecirc;n sử dụng m&agrave;u n&oacute;ng v&igrave; v&agrave;o m&ugrave;a đ&ocirc;ng sẽ chịu ảnh hưởng của gi&oacute; m&ugrave;a Đ&ocirc;ng Bắc v&agrave;o nh&agrave;, g&acirc;y cảm gi&aacute;c lạnh lẽo, trống trải. Hướng Bắc nhận được &iacute;t &aacute;nh s&aacute;ng n&ecirc;n d&ugrave;ng m&agrave;u s&aacute;ng sẽ gi&uacute;p kh&ocirc;ng gian rộng v&agrave; s&aacute;ng sủa hơn.</li>\r\n	<li><strong>Chọn m&agrave;u sắc cho trần nh&agrave; để hợp phong thuỷ với gia chủ:</strong>&nbsp;Theo quan niệm phong thủy phương Đ&ocirc;ng, trần nh&agrave; m&agrave;u tối lu&ocirc;n g&acirc;y cảm gi&aacute;c nặng nề, bức bối. Hơn nữa, m&agrave;u tối như đ&aacute;m m&acirc;y đen lu&acirc;n chuyển khiến cho tiền t&agrave;i, sức khỏe v&agrave; vận may của gia đ&igrave;nh dễ ti&ecirc;u tan. V&igrave; v&acirc;y, bạn n&ecirc;n sơn trần nh&agrave; c&oacute; t&ocirc;ng m&agrave;u s&aacute;ng hoặc m&agrave;u nhạt, trang nh&atilde;.</li>\r\n</ul>', '1627893314_7a756500456eb230eb7f.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 08:35:14', '2021-08-02 08:35:14'),
(3, 'Sắc màu phong thủy trong nội thất', 'Màu sắc trong không gian nội thất thể hiện sở thích, tính cách và yếu tố cảm xúc của người sử dụng. Màu sắc có sự tác động trực tiếp và nhanh chóng nhất đến thị giác và cảm nhận của người sử dụng không gian sống.', '<p>M&agrave;u sắc trong kh&ocirc;ng gian nội thất thể hiện sở th&iacute;ch, t&iacute;nh c&aacute;ch v&agrave; yếu tố cảm x&uacute;c của người sử dụng. M&agrave;u sắc c&oacute; sự t&aacute;c động trực tiếp v&agrave; nhanh ch&oacute;ng nhất đến thị gi&aacute;c v&agrave; cảm nhận của người sử dụng kh&ocirc;ng gian sống. Ngo&agrave;i ra, nhiều người &Aacute; Đ&ocirc;ng, trong đ&oacute; c&oacute; Việt Nam, thường lựa chọn m&agrave;u sắc cho ng&ocirc;i nh&agrave; dựa tr&ecirc;n c&aacute;c quy luật ngũ h&agrave;nh tương sinh &ndash; tương khắc theo quan niệm phong thủy. Trong phong thuỷ, m&agrave;u sắc h&igrave;nh th&agrave;nh n&ecirc;n c&aacute;c trường năng lượng t&aacute;c động đến m&ocirc;i trường nh&agrave; ở v&agrave; c&aacute; nh&acirc;n con người, gi&uacute;p c&acirc;n bằng h&agrave;i h&ograve;a nguồn năng lượng &acirc;m dương, gi&uacute;p ph&aacute;t huy những điều thuận lợi, hạn chế những điều bất lợi từ m&ocirc;i trường đến đời sống của ch&uacute;ng ta. Theo nguy&ecirc;n l&yacute; Ngũ h&agrave;nh, m&ocirc;i trường gồm c&oacute; 5 yếu tố v&agrave; m&agrave;u sắc tương ứng với từng yếu tố như sau:</p>\r\n\r\n<p>-KIM (Kim loại) c&oacute; m&agrave;u Trắng, V&agrave;ng, c&aacute;c sắc &aacute;nh kim</p>\r\n\r\n<p>-MỘC (C&acirc;y cối) c&oacute; m&agrave;u xanh lục</p>\r\n\r\n<p>-THỦY (Nước) c&oacute; m&agrave;u Xanh biển, Đen</p>\r\n\r\n<p>-HỎA (Lửa) c&oacute; m&agrave;u Đỏ, Hồng, T&iacute;m, Cam</p>\r\n\r\n<p>-THỔ (Đất) c&oacute; m&agrave;u N&acirc;u, V&agrave;ng đất, Cam đất</p>\r\n\r\n<p>Tương sinh, tương khắc của Ngũ h&agrave;nh</p>\r\n\r\n<p>-Tương sinh của ngũ h&agrave;nh: Mộc sinh Hỏa, Hỏa sinh Thổ, Thổ sinh Kim, Kim sinh Thủy, Thủy sinh Mộc (sơ đồ mũi t&ecirc;n v&ograve;ng tr&ograve;n theo chiều kim đồng hồ)</p>\r\n\r\n<p>-Tương khắc của ngũ h&agrave;nh: Kim khắc Mộc, Mộc khắc Thổ, Thổ khắc Thủy, Thủy khắc Hỏa, Hỏa khắc Kim (sơ đồ mũi t&ecirc;n của ng&ocirc;i sao 5 c&aacute;nh)</p>\r\n\r\n<p>Khi vận dụng m&agrave;u sắc theo phong thủy v&agrave;o nh&agrave; ở hoặc trang phục, ch&uacute;ng ta sẽ dựa tr&ecirc;n nguy&ecirc;n tắc tương sinh &ndash; tương khắc với bản mệnh của người sử dụng. Ch&uacute;ng ta thường chọn m&agrave;u bản mệnh (v&iacute; dụ người mệnh Hỏa sẽ chọn m&agrave;u của H&agrave;nh Hỏa l&agrave; Đỏ &ndash; Hồng &ndash; T&iacute;m &ndash; Cam), hoặc chọn m&agrave;u của h&agrave;nh tương sinh với bản mệnh (v&iacute; dụ người mệnh Hỏa sẽ chon m&agrave;u của H&agrave;nh Mộc l&agrave; Xanh lục, v&igrave; Mộc sinh Hỏa). Ch&uacute;ng ta kh&ocirc;ng n&ecirc;n hoặc hạn chế sử dụng những m&agrave;u sắc thuộc h&agrave;nh tương khắc với bản mệnh của m&igrave;nh (v&iacute; dụ mạng Hỏa sẽ tr&aacute;nh dung c&aacute;c m&agrave;u của h&agrave;nh Thủy, v&igrave; Thủy khắc Hỏa</p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8090/project/goldenstar/public/post/1627893148_3d094205626b9535cc7a(1).jpg\" style=\"height:450px; width:800px\" /></p>', '1627893383_824ffe3ede50290e7041.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 08:36:23', '2021-08-02 08:36:23'),
(4, 'Phạm Cao Đông thích màu trầm ấm, nhiếp ảnh gia Thiên Minh chuộng gam trung tính, Vlogger Giang Ơi yêu tông nhẹ nhàng, doanh nhân Trương Hoài Anh chọn màu lãng mạn.', 'Trải qua một năm 2020 nhiều biến động, mỗi người đều mong muốn một năm 2021 bình yên hơn và thành công hơn. Cuộc sống nói chung và tâm trạng nói riêng của mỗi cá nhân đôi khi chịu ảnh hưởng rất nhiều từ không gian sống, trong đó có màu sơn tường, ví dụ những màu sơn trầm ổn sẽ giúp tâm hồn chúng ta tĩnh lặng và suy nghĩ tập trung hơn.', '<p>Dưới đ&acirc;y l&agrave; c&aacute;ch chọn m&agrave;u sơn để l&agrave;m mới kh&ocirc;ng gian sống trong năm 2021 của một số người th&agrave;nh đạt.</p>\r\n\r\n<p>&ldquo;T&ocirc;i chọn gam m&agrave;u th&acirc;m trầm, ấm &aacute;p&rdquo;</p>\r\n\r\n<p>Doanh nh&acirc;n Phạm Cao Đ&ocirc;ng</p>\r\n\r\n<p>CEO CDC Home Design Center</p>\r\n\r\n<p>Phạm Cao Đ&ocirc;ng l&agrave; nh&agrave; s&aacute;ng lập CDC Home Design Center, chuy&ecirc;n ph&acirc;n phối c&aacute;c thương hiệu nội thất cao cấp. T&iacute;nh chất c&ocirc;ng việc khiến anh y&ecirc;u chuộng ứng dụng đa m&agrave;u sắc. Đối với anh, mỗi một gam m&agrave;u c&oacute; thể kể một c&acirc;u chuyện ri&ecirc;ng, phản &aacute;nh mong muốn của kh&aacute;ch h&agrave;ng, hoặc tr&agrave;o lưu thời trang, kiến tr&uacute;c, phong c&aacute;ch sống.</p>\r\n\r\n<p>Khi chọn gam m&agrave;u cho ng&ocirc;i nh&agrave; của m&igrave;nh, Phạm Cao Đ&ocirc;ng kh&ocirc;ng đắn đo trước sắc trầm ấm của đất. Đ&acirc;y ch&iacute;nh l&agrave; m&agrave;u mang đến cho anh thật nhiều cảm x&uacute;c. Những gam m&agrave;u trầm ổn, an y&ecirc;n, b&igrave;nh dị của đất nhắc nhở anh &ldquo;sống chậm lại&rdquo; để tận hưởng cuộc sống bằng tất cả c&aacute;c gi&aacute;c quan, b&igrave;nh t&acirc;m nh&igrave;n nhận thấu suốt về hiện tại, tương lai trong sự kết nối s&acirc;u sắc với qu&aacute; khứ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;T&ocirc;i th&iacute;ch những m&agrave;u sắc trung t&iacute;nh lịch thiệp&rdquo;</p>\r\n\r\n<p>Thi&ecirc;n Minh</p>\r\n\r\n<p>Nhiếp ảnh gia</p>\r\n\r\n<p>Ở độ tuổi 30, Thi&ecirc;n Minh đ&atilde; l&agrave; một nhiếp ảnh gia nổi tiếng với những t&aacute;c phẩm đen trắng nhiều chiều s&acirc;u v&agrave; cảm x&uacute;c. Anh đ&atilde; trải qua một qu&aacute; tr&igrave;nh nỗ lực kh&ocirc;ng ngừng, từ chiến thắng cuộc thi HHT Icon 2007 đến những năm th&aacute;ng tuổi trẻ vừa l&agrave;m vừa học học ở Mỹ để c&oacute; được ng&agrave;y h&ocirc;m nay. Hiện tại, Minh vẫn kh&ocirc;ng ngừng t&igrave;m kiếm vẻ đẹp v&agrave; cảm hứng cho từng t&aacute;c phẩm.</p>\r\n\r\n<p>&ldquo;T&ocirc;i chọn m&agrave;u xanh thư gi&atilde;n của trời v&agrave; biển&rdquo;</p>\r\n\r\n<p>Giang ơi</p>\r\n\r\n<p>Vlogger</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chuy&ecirc;n b&agrave;n luận những c&acirc;u chuyện x&atilde; hội, c&ocirc; g&aacute;i trẻ với những Vlog triệu view sở hữu lối tr&ograve; chuyện l&ocirc;i cuốn c&ugrave;ng vẻ ngo&agrave;i năng động, xinh đẹp. Thế nhưng đằng sau những video s&ocirc;i nổi ấy lại l&agrave; một Giang Ơi tĩnh lặng, trầm ổn, nhẹ nh&agrave;ng. Những khi kh&ocirc;ng quay phim, c&ocirc; chọn cho m&igrave;nh những g&oacute;c ri&ecirc;ng, d&agrave;nh thời gian để lắng nghe, cảm nhận v&agrave; chi&ecirc;m nghiệm.</p>\r\n\r\n<p>Giang đ&atilde; chọn xanh da trời hơi trầm v&agrave; phảng phất x&aacute;m l&agrave;m m&agrave;u sơn cho ph&ograve;ng l&agrave;m việc của hai vợ chồng. C&ocirc; t&acirc;m sự muốn biến ng&ocirc;i nh&agrave; của m&igrave;nh th&agrave;nh nơi y&ecirc;n b&igrave;nh, tĩnh lặng v&agrave; thanh lịch. Kh&ocirc;ng c&ograve;n b&atilde;o tố ph&iacute;a sau c&aacute;nh cửa, chỉ c&ograve;n lại sự tự do kho&aacute;ng đạt của trời biển, gi&uacute;p Giang c&oacute; th&ecirc;m nhiều nguồn năng lượng cho c&ocirc;ng việc, cũng như tiếp th&ecirc;m cho c&ocirc; thật nhiều sức mạnh đương đầu với b&atilde;o tố ngo&agrave;i kia.</p>', '1627894623_75dd7604576aa034f97b.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 08:57:03', '2021-08-02 08:57:03'),
(5, 'Chuyên gia màu sắc tư vấn 3 phong cách thiết kế căn hộ năm 2019', 'Bà Lisbeth Larsen - Giám đốc màu sắc Jotun toàn cầu, giới thiệu xu hướng thiết kế 2019 tập trung vào 3 phong cách: nhẹ nhàng, tinh tế, thô mộc.', '<p>B&agrave; Lisbeth Larsen - Gi&aacute;m đốc m&agrave;u sắc Jotun to&agrave;n cầu, giới thiệu xu hướng thiết kế 2019 tập trung v&agrave;o 3 phong c&aacute;ch: nhẹ nh&agrave;ng, tinh tế, th&ocirc; mộc.</p>\r\n\r\n<p>Trong sự kiện c&ocirc;ng bố bộ sưu tập m&agrave;u sắc 2019 vừa diễn ra ở TP HCM, b&agrave;&nbsp;Lisbeth Larsen chia sẻ:&nbsp;&quot;Ch&uacute;ng t&ocirc;i tin rằng m&agrave;u sắc l&agrave; c&aacute;ch pha trộn tự nhi&ecirc;n của cuộc đời để truyền cảm hứng cho ch&uacute;ng ta ho&agrave;n thiện v&agrave; thưởng thức cuộc sống&quot;.&nbsp;</p>\r\n\r\n<p>V&igrave; vậy trong bộ sưu tập cho năm sau, Jotun chọn con người l&agrave; yếu tố trung t&acirc;m để nghi&ecirc;n cứu về nh&oacute;m m&agrave;u ph&ugrave; hợp với xu hướng hiện đại, mang lại những cảm x&uacute;c cho chủ nh&acirc;n căn hộ. C&aacute;c nh&agrave; thiết kế truyền đi th&ocirc;ng điệp, ng&ocirc;i nh&agrave; l&agrave; sự phản &aacute;nh của gia chủ, thể hiện c&aacute; t&iacute;nh thay v&igrave; đi theo xu hướng cố định. V&igrave; vậy, m&agrave;u sắc v&agrave; vật dụng trang tr&iacute; nh&agrave; được gợi &yacute; dựa tr&ecirc;n 3 c&aacute; t&iacute;nh:&nbsp;nhẹ nh&agrave;ng, tinh tế, th&ocirc; mộc.</p>\r\n\r\n<p><img alt=\"Bà Lisbeth Larsen - Giám đốc màu sắc Jotun toàn cầu.\" src=\"https://i1-giadinh.vnecdn.net/2018/11/27/01-2944-1543297790.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kTHLLm_bU1yfdOvkZBFsag\" style=\"float:right\" /></p>\r\n\r\n<p>B&agrave; Lisbeth Larsen - Gi&aacute;m đốc m&agrave;u sắc Jotun to&agrave;n cầu.</p>\r\n\r\n<p>Thiết kế nhẹ nh&agrave;ng</p>\r\n\r\n<p>Nhẹ nh&agrave;ng được Jotun định nghĩa l&agrave; vẻ đẹp của sự đơn giản, gi&uacute;p t&acirc;m tr&iacute; gia chủ ho&agrave;n to&agrave;n thư th&aacute;i v&agrave; tĩnh lặng mỗi khi trở về tổ ấm. Phong c&aacute;ch n&agrave;y đề cao sự đơn giản, t&ocirc;ng chủ đạo l&agrave; m&agrave;u trung t&iacute;nh.</p>\r\n\r\n<p>Thiết kế được sử dụng kh&aacute; đơn giản, c&oacute; hơi hướm l&agrave;m nổi bật t&ocirc;ng m&agrave;u lạnh v&agrave; ưu ti&ecirc;n nội thất Bắc &Acirc;u hay Thiền định (Zen). Vật gia dụng lẫn b&agrave;n, ghế đều m&agrave;u trơn, kh&ocirc;ng họa tiết. C&aacute;c đường n&eacute;t của mỗi m&oacute;n đồ cũng được&nbsp;tối giản,&nbsp;thanh tho&aacute;t, hạn chế hoa văn rườm r&agrave;.</p>\r\n\r\n<p><img alt=\"Xu hướng nhẹ nhàng ưu tiên gam màu lạnh, hạn chế rườm rà.\" src=\"https://i1-giadinh.vnecdn.net/2018/11/26/jotun-4646-1543228985.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qscEjB3Hm7Xj7x_35GoxQQ\" style=\"float:right\" /></p>\r\n\r\n<p>Xu hướng nhẹ nh&agrave;ng ưu ti&ecirc;n gam m&agrave;u lạnh, hạn chế rườm r&agrave;.</p>\r\n\r\n<p><strong>Xu hướng tinh tế</strong></p>\r\n\r\n<p>Ở lối thiết kế thứ 2, b&agrave; Lisbeth Larsen cho biết ng&ocirc;i nh&agrave; cũng giống như một bảo t&agrave;ng nghệ thuật - nơi lưu trữ, trưng b&agrave;y những kỷ niệm v&agrave; hy vọng c&ugrave;ng những vật phẩm m&agrave; chủ nh&acirc;n đ&atilde; thu thập trong suốt cuộc h&agrave;nh tr&igrave;nh của m&igrave;nh.&nbsp;</p>\r\n\r\n<p>V&igrave; vậy, vị chuy&ecirc;n gia tư vấn nếu muốn đề cao sự s&aacute;ng tạo v&agrave; c&aacute; t&iacute;nh, bạn h&atilde;y ph&aacute; vỡ c&aacute;c lối m&ograve;n, tư duy truyền thống. Điều n&agrave;y đạt được qua sự kết hợp m&agrave;u sắc để tạo hiệu ứng nổi bật, lợi dụng sự tương phản để tăng t&iacute;nh ấn tượng cho kh&ocirc;ng gian.</p>\r\n\r\n<p>Khi tư duy ng&ocirc;i nh&agrave; l&agrave; một khung h&igrave;nh, bạn c&oacute; thể c&oacute; những thử nghiệm t&aacute;o bạo như d&ugrave;ng bộ b&agrave;n ăn đen tuyền từ inox, đường n&eacute;t mảnh đặt trong gian ph&ograve;ng c&oacute; tường sơn v&agrave;ng pastel. C&aacute;ch phối hợp ghế ngồi, đồng hồ v&agrave; cả r&egrave;m cửa cũng đầy ngẫu hứng, mang đến cảm hứng tươi mới mỗi ng&agrave;y.</p>\r\n\r\n<p><img alt=\"BỘ SƯU TẬP MÀU SẮC TRONG KIẾN TRÚC: TRỞ VỀ VỚI TỰ NHIÊN! - Xin bài edit, Nguyên làm giúp chị  - 1\" src=\"https://i1-giadinh.vnecdn.net/2018/11/26/26-11-201846-496461830-w500-2392-1543228985.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7qCBDLexzBGoQU_gPilX-Q\" /></p>\r\n\r\n<p>Gian ph&ograve;ng tinh tế thể hiện qua c&aacute;ch phối hợp m&agrave;u sắc, nội thất đầy s&aacute;ng tạo m&agrave; cũng t&agrave;i t&igrave;nh.</p>\r\n\r\n<p>Cảm hứng th&ocirc; mộc</p>\r\n\r\n<p>Sự th&ocirc; mộc của thi&ecirc;n nhi&ecirc;n l&agrave; nguồn cảm x&uacute;c gi&uacute;p c&acirc;n bằng kh&ocirc;ng gian hiện đại. Lối thiết kế th&ocirc; mộc của Jotun d&ugrave;ng t&ocirc;ng m&agrave;u ấm &aacute;p tự nhi&ecirc;n của đất v&agrave; c&aacute;t để tạo n&ecirc;n phong c&aacute;ch mạnh mẽ nhưng kh&ocirc;ng qu&aacute; nhiều &aacute;p lực, đảm bảo t&iacute;nh nhẹ nh&agrave;ng.&nbsp;</p>\r\n\r\n<p>Gợi nhắc về nguồn cội l&agrave;ng qu&ecirc;,&nbsp;ng&ocirc;i nh&agrave; th&ocirc; mộc c&oacute; thiết kế m&agrave;u sắc v&agrave; chất liệu thủ c&ocirc;ng lột tả ho&agrave;n to&agrave;n sự ch&acirc;n thực, vẻ đẹp kh&ocirc;ng ho&agrave;n hảo của tự nhi&ecirc;n khi chưa qua c&aacute;c c&ocirc;ng đoạn xử l&yacute;. Phong c&aacute;ch n&agrave;y tạo n&ecirc;n sợi d&acirc;y kết nối ng&ocirc;i nh&agrave; với m&ocirc;i trường xung quanh, phần &aacute;p m&aacute;i v&agrave; cột chống l&agrave;m từ gỗ, kh&ocirc;ng được m&agrave;i phẳng m&agrave; vẫn gồ ghề, th&ocirc; r&aacute;p.</p>\r\n\r\n<p>Để h&agrave;i h&ograve;a với c&aacute;c m&oacute;n đồ th&ocirc;, mộc, chuy&ecirc;n gia Jotun tư vấn n&ecirc;n chọn sơn tường cũng thi&ecirc;n về m&agrave;u gỗ, đất. Tr&aacute;nh m&agrave;u đậm v&agrave; n&ecirc;n ưu ti&ecirc;n tường hơi ngả sang gam pastel để kh&ocirc;ng gian sống nhẹ nh&agrave;ng, thư th&aacute;i, tĩnh lặng hơn.</p>\r\n\r\n<p><img alt=\"Phòng cách thiết kế theo chủ đề Thô mộc.\" src=\"https://i1-giadinh.vnecdn.net/2018/11/27/image003-8561-1543297790.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YbIWvMELBITctBgSVESIBQ\" /></p>\r\n\r\n<p>Ph&ograve;ng c&aacute;ch thiết kế theo chủ đề &quot;Th&ocirc; mộc&quot;.</p>\r\n\r\n<p>Ngo&agrave;i những gợi &yacute; ph&iacute;a tr&ecirc;n, nhiều năm qua Jotun cũng đ&atilde; đầu tư nghi&ecirc;n cứu m&agrave;u sắc, ch&uacute; trọng kh&aacute;i niệm c&aacute; nh&acirc;n h&oacute;a để gi&uacute;p con người cảm thụ được những m&agrave;u sắc thời thượng, c&aacute; t&iacute;nh hơn.&nbsp;C&aacute;c bộ sưu tập m&agrave;u sắc gi&uacute;p &iacute;ch cho gia chủ v&agrave; kiến tr&uacute;c sư, tăng th&ecirc;m cảm hứng lựa chọn phong c&aacute;ch cho tổ ấm.</p>\r\n\r\n<p>Hơn một năm trước, h&atilde;ng sơn đ&atilde; giới thiệu bộ sưu tập m&agrave;u sắc 2017 gồm 3 gợi &yacute;: Ng&ocirc;i nh&agrave; Nordic với m&agrave;u sắc du mục gần gũi, h&agrave;i ho&agrave; với thi&ecirc;n nhi&ecirc;n; Phong c&aacute;ch đ&ocirc; thị hiện đại, linh hoạt v&agrave; độc đ&aacute;o; Kh&ocirc;ng gian văn ho&aacute; đa sắc th&aacute;i, sinh động v&agrave; ấm c&uacute;ng.</p>\r\n\r\n<p>Đến năm 2018, c&ocirc;ng ch&uacute;ng lại được tiếp x&uacute;c với: Th&agrave;nh phố chuyển động - gam trầm b&igrave;nh dị, s&acirc;u lắng của rừng nhiệt đới; Tổ ấm b&igrave;nh y&ecirc;n - m&agrave;u của sa mạc, hoa đ&agrave;o v&agrave; n&acirc;u c&aacute;t đem lại sự thư gi&atilde;n, y&ecirc;n b&igrave;nh v&agrave; ấm c&uacute;ng; Khu vườn xanh m&aacute;t - đem đến cảm gi&aacute;c thanh b&igrave;nh, tĩnh lặng với sắc m&agrave;u thi&ecirc;n nhi&ecirc;n như &aacute;nh nắng mặt trời, c&acirc;y cối...</p>', '1627894899_image003-8561-1543297790.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:01:39', '2021-08-02 09:01:39'),
(6, 'Nhà vừa xây xong tường đã nứt chân chim, phải xử lý ra sao?', 'Nhà tôi mới xây xong, trên tường hiện đã xuất hiện vài vết rạn sơn, có cách nào xử lý mà không phải đục tường hay không?', '<p>Vết rạn ch&acirc;n chim như thế c&oacute; phải nh&agrave; n&agrave;o cũng bị hay kh&ocirc;ng, nguy&ecirc;n nh&acirc;n l&agrave; g&igrave;?&nbsp;<em>(Thuận)</em></p>\r\n\r\n<p>Trả lời</p>\r\n\r\n<p>Trường hợp n&agrave;y rất thường hay gặp, do nhiều nguy&ecirc;n nh&acirc;n. Do yếu tố về kh&iacute; hậu, do ch&uacute;ng ta t&ocirc; tr&aacute;t vữa, kh&aacute;ng kiềm, bả mat&iacute;t v&agrave; lăn sơn kh&ocirc;ng đ&uacute;ng quy tr&igrave;nh, kỹ thuật th&igrave; thường một th&aacute;ng sau khi l&agrave;m nh&agrave; xong, bề mặt tường c&oacute; dấu hiệu rạn ch&acirc;n chim. Nếu như vết rạn nhỏ, kh&ocirc;ng k&eacute;o d&agrave;i, kh&ocirc;ng nứt v&agrave;o b&ecirc;n trong cấu tạo của tường th&igrave; bạn kh&ocirc;ng cần phải lo lắng, đ&acirc;y l&agrave; một hiện tượng b&igrave;nh thường, chỉ l&agrave; bề mặt th&ocirc;i.</p>\r\n\r\n<p>Bộ X&acirc;y dựng c&oacute; quy định c&aacute;c nh&agrave; thầu phải bảo h&agrave;nh trong 24 th&aacute;ng. Trong thời gian đ&oacute;, nh&agrave; thầu sẽ chọn một thời điểm th&iacute;ch hợp đến để khắc phục những dấu hiệu nứt rạn ch&acirc;n. Th&ocirc;ng thường c&aacute;c nh&agrave; thầu sẽ để tường rạn hết một năm đầu, năm sau tới bả matit lại, sơn nước lại, sẽ hết.</p>\r\n\r\n<p>Hiện nay, tr&ecirc;n thị trường c&oacute; nhiều loại sơn phủ bề mặt cho ngoại thất v&agrave; nội thất m&agrave; hệ số co gi&atilde;n của sơn l&ecirc;n đến 300%. Tức l&agrave; nếu ch&uacute;ng ta sơn mảng sơn khoảng 10cm, khi kh&ocirc; rồi, ta c&oacute; thể k&eacute;o gi&atilde;n mảng sơn đến 30cm m&agrave; kh&ocirc;ng xảy ra hiện tượng chảy, nứt. Loại sơn n&agrave;y cho ph&eacute;p tường nh&agrave;, đặc biệt bề mặt ngo&agrave;i trời c&oacute; thể chịu được sự thay đổi khắc nghiệt của kh&iacute; hậu, m&agrave; kh&ocirc;ng g&acirc;y rạn nứt bề mặt. Đương nhi&ecirc;n, gi&aacute; th&agrave;nh của những loại sơn n&agrave;y sẽ tương đối đắt, b&ugrave; lại rất bền v&agrave; ch&uacute;ng ta kh&ocirc;ng phải bảo h&agrave;nh bảo dưỡng li&ecirc;n tục ngoại thất ng&ocirc;i nh&agrave;.</p>\r\n\r\n<p>Mọi người đều biết, mỗi lần sơn lại nh&agrave; rất vất vả, đặc biệt sơn trong nội thất. Ch&uacute;ng ta muốn sơn được phải dọn hết đồ đạc ra, phải bả matit. Sau khi l&agrave;m xong, bụi bay mịt m&ugrave;, v&agrave; cũng phải chờ một thời gian nhất định m&ugrave;i sơn mới bay hết. N&ecirc;n chọn một loại sơn tốt, lau ch&ugrave;i được.</p>\r\n\r\n<p>Để hạn chế hiện tượng nứt tường như tr&ecirc;n, chủ đầu tư n&ecirc;n đặt vấn đề với nh&agrave; thầu n&ecirc;n thực hiện đ&uacute;ng quy tr&igrave;nh thi c&ocirc;ng của c&aacute;c h&atilde;ng sơn. Mua đ&uacute;ng loại sơn cho từng vị tr&iacute; tường ph&ugrave; hợp, đừng tiết kiệm kh&ocirc;ng đ&uacute;ng c&aacute;ch đ&ocirc;i khi bạn lại phải trả tiền nhiều hơn thế nữa!</p>', 'default.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:02:23', '2021-08-02 09:02:23'),
(7, 'Màu sơn giúp bạn bán nhà vừa nhanh vừa giá cao', 'Nếu không thể sửa sang nhiều, bạn hãy quét màu sơn trắng ngà cho những mảng tường lớn trong nhà.', '<p>Hầu hết mọi người đều biết rằng, khi chuẩn bị b&aacute;n nh&agrave;, bạn n&ecirc;n đầu tư c&ocirc;ng sức để nh&agrave; cửa được ngăn nắp, gọn g&agrave;ng v&agrave; tr&ocirc;ng c&oacute; sức sống hơn. Điều n&agrave;y đồng nghĩa với việc bạn sẽ phải chi một &iacute;t tiền để trang tr&iacute; lại nội ngoại thất. Tuy nhi&ecirc;n, sau đ&oacute;, bạn sẽ dễ d&agrave;ng tăng được gi&aacute; b&aacute;n nh&agrave;.</p>\r\n\r\n<p><img alt=\"mau-son-giup-ban-ban-nha-vua-nhanh-vua-gia-cao\" src=\"https://i1-giadinh.vnecdn.net/2017/11/25/mau-sang-7261-1511578461.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ADzcAVEgxwvaLW6Sh_Pmlg\" /></p>\r\n\r\n<p>Những căn nh&agrave; c&oacute; m&agrave;u trung t&iacute;nh&nbsp;gi&uacute;p người chủ tương lai dễ d&agrave;ng bố tr&iacute; đồ đạc. Ảnh minh họa:&nbsp;<em>Infotel.</em></p>\r\n\r\n<p>Theo b&agrave; Victoria Gimson, Gi&aacute;m đốc thiết kế của c&ocirc;ng ty nội thất Decorum (Australia), c&oacute; nhiều mẹo khi b&aacute;n nh&agrave;. Nhưng nếu bạn kh&ocirc;ng c&oacute; nhiều thời gian v&agrave; tiền bạc, chỉ cần qu&eacute;t tường sang một m&agrave;u sơn mới c&oacute; thể đem lại hiệu quả lớn. M&agrave;u bạn cần chọn l&agrave; m&agrave;u trắng ng&agrave;.</p>\r\n\r\n<p>M&agrave;u trắng ng&agrave; mang t&iacute;nh chất cổ điển, hợp với mọi thời đại, kh&ocirc;ng bị lỗi mốt. B&agrave; Gimson tin rằng, những bức tường m&agrave;u ng&agrave; khiến ng&ocirc;i nh&agrave; tr&ocirc;ng c&oacute; vẻ đắt tiền hơn. Ngo&agrave;i ra, những bức tường trắng sẽ cung cấp những mảng nền sạch cho c&aacute;c kh&aacute;ch h&agrave;ng tiềm năng bố tr&iacute; đồ đạc sau n&agrave;y.&nbsp;</p>\r\n\r\n<p>Sau khi mua, người chủ mới c&oacute; thể dễ d&agrave;ng trang tr&iacute; nh&agrave; cửa. Hầu hết c&aacute;c m&oacute;n đồ nội thất d&ugrave; c&oacute; m&agrave;u sắc thế n&agrave;o cũng dễ d&agrave;ng h&agrave;i h&ograve;a tr&ecirc;n nền m&agrave;u trắng ng&agrave;. Chủ nh&agrave; kh&ocirc;ng phải t&iacute;nh to&aacute;n việc sơn lại ngay lập tức những mảng m&agrave;u qu&aacute; nổi bật hoặc đối chọi với đồ d&ugrave;ng hiện c&oacute;.</p>\r\n\r\n<p>Bởi vậy, theo&nbsp;<em>House Beautiful</em>, kh&aacute;ch h&agrave;ng sẽ cho rằng m&igrave;nh kh&ocirc;ng phải tốn k&eacute;m nhiều tiền cho việc cải tạo nh&agrave; mới mua. Do đ&oacute;, việc giao dịch nh&agrave; cửa thuận lợi hơn với mức gi&aacute; cao hơn h&agrave;ng ngh&igrave;n USD (t&ugrave;y diện t&iacute;ch nh&agrave;).</p>\r\n\r\n<p>Chuy&ecirc;n gia nội thất cũng khuy&ecirc;n, người b&aacute;n nh&agrave; đừng vội k&ecirc; hết đồ đạc đi. H&atilde;y để lại những nội thất cơ bản, sử dụng những m&oacute;n đồ c&oacute; m&agrave;u sắc tươi vui, c&acirc;y trồng trang tr&iacute; gi&uacute;p cho ng&ocirc;i nh&agrave; c&oacute; cảm gi&aacute;c ấm c&uacute;ng.</p>', '1627895043_mau-sang-7261-1511578461.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:04:03', '2021-08-02 09:04:03'),
(8, 'Chung cư xấu xí lột xác thành nhà đẹp bằng vài nét vẽ', 'Những ngôi nhà bỗng \'xuất hiện\' thêm rất nhiều ban công, cửa sổ nhiều màu sắc.', '<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/1-1-1468226616.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LABeuVYOMGTpBKioLiD2MQ\" /></p>\r\n\r\n<p>Nghệ sĩ Patrick Commecy c&ugrave;ng những người bạn y&ecirc;u th&iacute;ch vẽ tranh tường đ&atilde; đem lại sức sống mới cho nhiều t&ograve;a nh&agrave; tr&ecirc;n khắp nước Ph&aacute;p.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/1-2-1468226616.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5064E8SGHWOtCIABoYZVPA\" /></p>\r\n\r\n<p>Họ biến những bức tường xấu x&iacute; th&agrave;nh tranh vẽ ban c&ocirc;ng, cửa sổ, đường phố với sự xuất hiện của con người.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/1-3-1468226617.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kDxcSCkC5OUGFfNC32kE0g\" /></p>\r\n\r\n<p>Nếu nh&igrave;n từ xa, người đi đường cảm tưởng khu phố n&agrave;y c&oacute; qu&aacute;n c&agrave; ph&ecirc; ngo&agrave;i trời.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/1-4-1468226617.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=rQ6cRxPVvwQcy72Q__qc8A\" /></p>\r\n\r\n<p>Những mảng tường lấy cảm hứng từ t&aacute;c phẩm&nbsp;<em>Clochemerle&nbsp;</em>của nh&agrave; văn Gabriel Chevallier. H&igrave;nh ảnh c&aacute;c nh&acirc;n vật tr&ecirc;n ban c&ocirc;ng được lấy từ nguy&ecirc;n mẫu trong truyện.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/1-5-1468226618.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bwMiFmb2YvJsGGldL8oIFw\" /></p>\r\n\r\n<p>Kh&ocirc;ng cần cải tạo lại, mặt b&ecirc;n của t&ograve;a nh&agrave; vẫn c&oacute; th&ecirc;m những mảng tường gạch mộc, cửa sổ điệu đ&agrave; c&ugrave;ng phong c&aacute;ch với mặt tiền.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/5-1-1468226619.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k6S1xpysxeO0UKEra2KNRw\" /></p>\r\n\r\n<p>Ngo&agrave;i những lối ra v&agrave;o, cửa sổ thật, khu nh&agrave; c&ograve;n c&oacute; th&ecirc;m những khung cửa sổ nhiều m&agrave;u sắc kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/5-2-1468226619.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ON-7MViP1Dt-M7uSEoDM9A\" /></p>\r\n\r\n<p>Mất đi chỗ để quảng c&aacute;o nhưng người d&acirc;n sẽ vẫn h&agrave;i l&ograve;ng với h&igrave;nh ảnh mới của g&oacute;c phố.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/5-3-1468226620.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fgVoVSSbhw_lbjvwBwMxeg\" /></p>\r\n\r\n<p>H&igrave;nh ảnh vui chơi của em nhỏ cũng l&agrave; nguồn cảm hứng của c&aacute;c nghệ sĩ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/6-1-1468226620.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QaCvcZETWNBfdFkvRyeelA\" /></p>\r\n\r\n<p>C&acirc;u chuyện t&igrave;nh giữa Romeo v&agrave; Juliet diễn ra ở khung cảnh hiện đại hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/6-2-1468226621.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U0TAGeP54HvJhaYPmIUuSw\" /></p>\r\n\r\n<p>Nhưng sự l&atilde;ng mạn v&agrave; thi vị vẫn tr&agrave;n ngập với sự thể hiện t&agrave;i t&igrave;nh của c&aacute;c nghệ sĩ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2016/07/11/Untitled-4-1468227860.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4YMcYJQrEeU6TeWoY3AglA\" /></p>\r\n\r\n<p>Những nụ h&ocirc;n bất hủ trong&nbsp;<em>Cuốn theo chiều gi&oacute;, Titanic</em>... được t&aacute;i hiện lại với h&igrave;nh ảnh đen trắng.</p>\r\n\r\n<p>&nbsp;</p>', '1627895117_mau-sang-7261-1511578461.jpg', 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:05:17', '2021-08-02 09:05:17'),
(9, 'TỔ CHỨC HOẠT ĐỘNG NGOẠI KHÓA', NULL, '<h1><a href=\"https://spr.vn/to-chuc-hoat-dong-ngoai-khoa-cho-doanh-nghiep-thi-nen-lam-gi/\" rel=\"bookmark\">TỔ CHỨC HOẠT ĐỘNG NGOẠI KH&Oacute;A CHO DOANH NGHIỆP TH&Igrave; N&Ecirc;N L&Agrave;M G&Igrave;?</a></h1>\r\n\r\n<ul>\r\n	<li>15 Apr, 2021</li>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n	<li><a href=\"https://spr.vn/tt/tin-tuc/\" rel=\"category tag\">Tin tức</a></li>\r\n</ul>\r\n\r\n<p>Nh&igrave;n v&agrave;o những c&ocirc;ng ty, doanh nghiệp lớn đang tồn tại trong nền kinh tế hiện nay th&igrave; c&oacute; thể dễ d&agrave;ng nhận ra được rằng họ đều đầu tư rất mạnh tay v&agrave;o việc chăm lo gi&aacute; trị tinh thần của tập thể nh&acirc;n vi&ecirc;n. Trong đ&oacute; nổi l&ecirc;n l&agrave; việc x&acirc;y dựng, thiết kế những bộ đồng phục đẹp mắt&nbsp;mắt, đầu tư v&agrave;o cơ sở vật chất v&agrave; tổ chức c&aacute;c hoạt động ngoại kho&aacute; một c&aacute;ch thường xuy&ecirc;n, quy m&ocirc;, chất lượng.</p>\r\n\r\n<p><strong>Hoạt động ngoại kho&aacute;</strong>&nbsp;c&oacute; thể cắt nghĩa một c&aacute;ch đơn giản l&agrave; những hoạt động được tổ chức trong một tập thể. Thế nhưng n&oacute; lại nằm ngo&agrave;i phạm vi chủ thể ch&iacute;nh thống. Nh&igrave;n s&acirc;u v&agrave;o tr&ugrave;m kh&aacute;i niệm n&agrave;y th&igrave; c&oacute; thể thấy n&oacute; bao h&agrave;m c&aacute;c hoạt động văn ho&aacute;, thể thao, giải tr&iacute; được tổ chức trong v&agrave; ngo&agrave;i phạm vi tập thể. Đ&acirc;y được coi l&agrave; một m&ocirc;i trường m&agrave; sự tham gia được dựa tr&ecirc;n sự tự nguyện, nhu cầu v&agrave; khả năng bản th&acirc;n.</p>\r\n\r\n<p>Vai tr&ograve; nổi bật nhất của&nbsp;hoạt động ngoại kho&aacute;&nbsp;l&agrave; tạo ra m&ocirc;i trường để x&acirc;y dựng v&agrave; ph&aacute;t triển quan hệ, mối kết nối giữa c&aacute;c c&aacute;c nh&acirc;n trong tập thể v&agrave; giữa tập thể với những c&aacute; thể, tổ chức, cơ sở b&ecirc;n ngo&agrave;i hay với to&agrave;n x&atilde; hội. Ở quy m&ocirc; c&ocirc;ng ty, doanh nghiệp th&igrave; những hoạt động ngoại kho&aacute; sẽ c&oacute; &yacute; nghĩa l&agrave; tạo ra khoảng nghỉ cần thiết để lấy lại sự c&acirc;n băng hay duy tr&igrave; nhiệt huyết c&ocirc;ng việc.</p>\r\n\r\n<p>&nbsp;</p>', NULL, 0, 0, NULL, NULL, NULL, NULL, 'Long Xuyên-An Giang', '2021-08-14', NULL, NULL, 'ac', '2021-08-02 09:15:24', '2021-08-02 09:15:24'),
(10, 'Trần Hiếu', NULL, '<p>L&agrave; nh&agrave; ph&acirc;n phối tin cậy của c&ocirc;ng ty. Đ&atilde; kết hợp rất nhiều năm qua</p>', NULL, 0, 0, NULL, NULL, NULL, NULL, 'TT.An Phú, An Giang', NULL, '0933244242', 'tranhieu@gmail.com', 'di', '2021-08-02 09:20:09', '2021-08-02 09:20:09'),
(11, 'Thới Sơn', NULL, '<p>L&agrave; nh&agrave; ph&acirc;n phối tin cậy của c&ocirc;ng ty. Đ&atilde; kết hợp rất nhiều năm qua</p>', NULL, 0, 0, NULL, NULL, NULL, NULL, 'TT.Tri Tôn, An Giang', NULL, '0983874232', 'thoison@gmail.com', 'di', '2021-08-02 09:21:36', '2021-08-02 09:21:36'),
