INSERT INTO `posts` (`id`, `name`, `summary`, `content`, `image`, `show`, `view`, `price`, `promotion_price`, `id_trademark`, `id_distributor`, `place`, `timetake`, `telephone`, `email`, `type`, `created_at`, `updated_at`) VALUES
(1, 'VỀ CHÚNG TÔI', 'GoldenStar là một trong những địa chỉ hoạt động trong lĩnh vực nội thất. Chúng tôi tự hào khi là đơn vị phân phối và cung cấp trọn gói từ gạch trang trí, gạch lót, sơn nước…đến bán lẻ các thiết bị vệ sinh với chi phí tối ưu. Giúp bạn làm mới không gian sống một cách dễ dàng hơn.', '<p><img alt=\"\" src=\"http://localhost:8090/project/goldenstar/public/img/1628174498_3128158532ebc5b59cfa.jpg\" style=\"float:left; height:300px; margin-left:10px; margin-right:10px; width:400px\" />Với ti&ecirc;u ch&iacute; &ldquo;Một lần sử dụng, vạn lần tin&rdquo; ch&uacute;ng t&ocirc;i mang đến sự lựa chọn tốt nhất về c&aacute;c sản phẩm nội thất mới nhất tr&ecirc;n thị trường. GoldenStar hiện đang c&oacute; hơn 1000 sản phẩm gạch từ c&aacute;c thương hiệu kh&aacute;c nhau như Unis, Unimax, Viglaceara, Prime&hellip;B&ecirc;n cạnh đ&oacute; l&agrave; d&ograve;ng sơn Sakara &ndash; d&ograve;ng sơn tiềm năng của ch&uacute;ng t&ocirc;i với c&ocirc;ng nghệ hiện đại từ Nhật Bản sẽ giải quyết gần như tất cả nhu cầu, c&acirc;u hỏi của kh&aacute;ch h&agrave;ng như chi ph&iacute;, bền m&agrave;u, chống thấm&hellip;v&agrave; đa dạng nhiều thương hiệu kh&aacute;c được ưa chuộng Akyo, Moni, Toshiko, Ai panit, Ninza. Ch&uacute;ng t&ocirc;i rất lấy l&agrave;m vinh dự khi được Kh&aacute;ch h&agrave;ng v&agrave; Qu&yacute; đối t&aacute;c ưu &aacute;i, tin tưởng v&agrave; giới thiệu phổ biến rộng r&atilde;i tại khu vực Đồng bằng s&ocirc;ng Cửu Long.</p>\r\n\r\n<p>Trong bối cảnh ph&aacute;t triển ng&agrave;y c&agrave;ng lớn mạnh của Samsung v&agrave; c&aacute;c Vendor, nhu cầu li&ecirc;n kết với c&aacute;c đối t&aacute;c cung cấp dịch vụ của nh&agrave; m&aacute;y Samsung l&agrave; rất lớn v&agrave; rất đa dạng. Trong khi đ&oacute;, c&aacute;c c&ocirc;ng ty hiện tại chỉ cung cấp một mảng dịch vụ nhất định g&acirc;y kh&oacute; khăn cho c&ocirc;ng t&aacute;c quản l&yacute; tổng thể của kh&aacute;ch h&agrave;ng. V&igrave; vậy, ch&uacute;ng t&ocirc;i, một đội ngũ l&atilde;nh đạo thuộc c&aacute;c c&ocirc;ng ty cung cấp c&aacute;c dịch vụ kh&aacute;c nhau đ&atilde; li&ecirc;n minh lại v&agrave; đồng s&aacute;ng lập ra &Aacute;nh Dương Vina nhằm cung cấp tổng hợp c&aacute;c dịch vụ.</p>\r\n\r\n<p>Đến với &Aacute;nh Dương Vina, kh&aacute;ch h&agrave;ng sẽ tiết kiệm được thời gian v&agrave; dễ d&agrave;ng quản l&yacute; t&agrave;i ch&iacute;nh, chất lượng, ti&ecirc;u chuẩn sản phẩm theo một hệ thống bởi ch&uacute;ng t&ocirc;i cung cấp đầy đủ c&aacute;c dịch vụ như: (1) Cung ứng nh&acirc;n lực, (2) Dịch vụ bảo vệ, (3) Dịch vụ n&acirc;ng cẩu h&agrave;ng h&oacute;a, (4) Dịch vụ vận tải, (5) Thi c&ocirc;ng lắp đặt hệ thống điện; (6) Thi c&ocirc;ng lắp đặt kết cấu, (7) Thiết kế, lắp đặt v&agrave; cung cấp vật tư, thiết bị ph&ograve;ng sạch, (8) Cung cấp sắt, th&eacute;p v&agrave; c&aacute;c loại vật dụng x&acirc;y dựng,&hellip;</p>\r\n\r\n<p>Cuối c&ugrave;ng, con người ch&iacute;nh l&agrave; t&agrave;i sản quan trọng nhất của showroom ch&uacute;ng t&ocirc;i. Mỗi nh&acirc;n vi&ecirc;n của GoldenStart&nbsp;đều chung một đam m&ecirc; với lĩnh vực trang tr&iacute; nội thất, đều mong muốn mang đến gi&aacute; trị ri&ecirc;ng biệt v&agrave; dịch vụ mua sắm tiện lợi, thoải m&aacute;i nhất đến với kh&aacute;ch h&agrave;ng khi đ&atilde; chọn showroom ch&uacute;ng t&ocirc;i.<img alt=\"\" src=\"http://localhost:8090/project/goldenstar/public/img/1628174681_7fe997a8b7c6409819d7.jpg\" style=\"float:right; height:300px; margin:10px; width:400px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i tin rằng ng&ocirc;i nh&agrave; ch&iacute;nh l&agrave; niềm hi vọng v&agrave; l&agrave; ước mơ của rất nhiều người. Nh&agrave; Đẹp kh&ocirc;ng chỉ l&agrave; ph&ograve;ng kh&aacute;ch đẹp, ph&ograve;ng ngủ đẹp, nh&agrave; bếp đẹp. M&agrave; l&agrave; đẹp bởi Nh&agrave; l&agrave; nơi c&oacute; những người th&acirc;n của ch&uacute;ng ta, những người ta y&ecirc;u thương m&agrave; kh&ocirc;ng cần l&yacute; lẽ. Nh&agrave; l&agrave; nơi c&oacute; bố, c&oacute; mẹ, c&oacute; vợ chồng, con c&aacute;i. Nh&agrave; l&agrave; tổ ấm, ta từng ch&uacute;t vun v&eacute;n v&agrave; x&acirc;y dựng th&agrave;nh. Đ&oacute; l&agrave; l&yacute; do tại sao ch&uacute;ng t&ocirc;i ở đ&acirc;y v&agrave; phục vụ bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mọi chi tiết vui l&ograve;ng li&ecirc;n hệ ch&uacute;ng t&ocirc;i:</strong></p>\r\n\r\n<p><strong>Showroom:</strong>&nbsp;161 Điện Bi&ecirc;n Phủ, Phường Mỹ Ph&uacute;, Tp. Cao L&atilde;nh, Đồng Th&aacute;p</p>\r\n\r\n<p><strong>Showroom:</strong>&nbsp;264 H&agrave; Ho&agrave;ng Hổ, Phường Mỹ H&ograve;a, Tp. Long Xuy&ecirc;n, An Giang</p>\r\n\r\n<p><strong>Điện thoại:</strong>&nbsp;02773877979 &ndash; 02963959697</p>\r\n\r\n<p><strong>Hotline:</strong>&nbsp;0907 123 344</p>', '1627893148_3d094205626b9535cc7a(1).jpg', 1, 3, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gt', '2021-08-02 08:32:28', '2021-08-05 14:46:47'),
(2, 'CHÍNH SÁCH CHẤT LƯỢNG', 'GoldenStar cam kết cung cấp các sản phẩm, giải pháp và dịch vụ nhãn hàng hoàn thiện, thường xuyên cải tiến để luôn thỏa mãn sự mong đợi của bạn hàng.', '<p><strong>Mục Ti&ecirc;u Chất Lượng</strong></p>\r\n\r\n<p>GoldenStar thỏa m&atilde;n bạn h&agrave;ng theo 5 mục ti&ecirc;u ch&iacute;nh:</p>\r\n\r\n<p>- Chất lượng ổn định</p>\r\n\r\n<p>- Giao h&agrave;ng đ&uacute;ng hạn</p>\r\n\r\n<p>- Dịch vụ ho&agrave;n thiện</p>\r\n\r\n<p>- Cải tiến thường xuy&ecirc;n</p>\r\n\r\n<p>- Cạnh tranh hiệu quả</p>\r\n\r\n<p><strong>Hệ thống quản l&yacute; chất lượng</strong></p>\r\n\r\n<p>Hệ thống hoạch định nguồn lực doanh nghiệp (ERP &ndash; Enterprise Resource Planning) gi&uacute;p n&acirc;ng cao năng suất tổng thể v&agrave; l&agrave;m đ&ograve;n bẩy quản l&yacute; chất lượng, cụ thể:</p>\r\n\r\n<p>- Sắp xếp chuỗi cung ứng từ thu mua, quy tr&igrave;nh đặt mua h&agrave;ng từ bạn h&agrave;ng, sản xuất v&agrave; tồn kho.</p>\r\n\r\n<p>- Ho&agrave;n th&agrave;nh c&ocirc;ng việc đ&uacute;ng thời hạn với sự gi&aacute;m s&aacute;t tốt hơn.</p>\r\n\r\n<p>- Cải thiện dịch vụ kh&aacute;ch h&agrave;ng khi đưa ra quyết định nhanh ch&oacute;ng bằng b&aacute;o c&aacute;o v&agrave; ph&acirc;n t&iacute;ch dễ sử dụng.</p>\r\n\r\n<p>- Kiểm so&aacute;t được nguồn t&agrave;i nguy&ecirc;n, d&ograve;ng tiền v&agrave; kế to&aacute;n.</p>\r\n\r\n<p><strong>ISO 9001:2008 Hệ thống quản l&yacute; chất lượng</strong></p>\r\n\r\n<p>Thể hiện khả năng của AL c&oacute; thể cung cấp sản phẩm đ&aacute;p ứng nhu cầu của bạn h&agrave;ng, c&aacute;c quy định v&agrave; ph&aacute;p luật hiện h&agrave;nh.</p>\r\n\r\n<p>Gia tăng sự thỏa m&atilde;n của bạn h&agrave;ng th&ocirc;ng qua ứng dụng hệ thống hiệu quả bao gồm c&aacute;c quy tr&igrave;nh nhằm cải tiến li&ecirc;n tục cho hệ thống v&agrave; đảm bảo đ&aacute;p ứng nhu cầu của bạn h&agrave;ng, c&aacute;c quy định ph&aacute;p luật hiện h&agrave;nh.</p>\r\n\r\n<p><strong>ISO 14001:2004 Hệ thống quản l&yacute; m&ocirc;i trường</strong></p>\r\n\r\n<p>Tr&ecirc;n tinh thần &ldquo;CẢI THIỆN V&Agrave; BẢO VỆ M&Ocirc;I TRƯỜNG&rdquo;, ch&uacute;ng t&ocirc;i cam kết x&acirc;y dựng, thực hiện v&agrave; duy tr&igrave; hệ thống quản l&yacute; m&ocirc;i trường ISO 14001:2004 dựa tr&ecirc;n những nguy&ecirc;n tắc cơ bản sau đ&acirc;y:</p>\r\n\r\n<p>- Tu&acirc;n thủ triệt để c&aacute;c qui định ph&aacute;p luật v&agrave; c&aacute;c y&ecirc;u cầu kh&aacute;c li&ecirc;n quan đến c&aacute;c kh&iacute;a cạnh m&ocirc;i trường c&ocirc;ng ty.</p>\r\n\r\n<p>- Lu&ocirc;n xem x&eacute;t cải tiến trong mọi hoạt động, t&iacute;ch cự triển khai c&aacute;c mục ti&ecirc;u v&agrave; chương tr&igrave;nh cải tiến nhằm ho&agrave;n thiện hệ thống m&ocirc;i trường.</p>\r\n\r\n<p>- Bảo vệ m&ocirc;i trường bằng c&aacute;ch kiểm so&aacute;t tốt vấn đề chất thải, t&agrave;i nguy&ecirc;n v&agrave; sự cố m&ocirc;i trường th&ocirc;ng qua thực hiện c&aacute;c biện ph&aacute;p kiểm so&aacute;t điều h&agrave;nh th&iacute;ch hợp v&agrave; gi&aacute;m s&aacute;t c&oacute; hiệu quả.</p>\r\n\r\n<p>- Tăng cường c&ocirc;ng t&aacute;c đ&agrave;o tạo v&agrave; th&ocirc;ng tin m&ocirc;i trường đến nh&acirc;n vi&ecirc;n trong c&ocirc;ng ty v&agrave; c&aacute;c b&ecirc;n li&ecirc;n quan, đảm bảo nguồn lực đ&aacute;p ứng y&ecirc;u cầu của hệ th&ocirc;ng quản l&yacute; m&ocirc;i trường.</p>\r\n\r\n<p>- An Lạc mong muốn g&oacute;p phần x&acirc;y dựng m&ocirc;i trường sống ng&agrave;y c&agrave;ng tốt hơn.</p>\r\n\r\n<p><strong>5s</strong></p>\r\n\r\n<p><strong>Mục Ti&ecirc;u Chất Lượng</strong></p>\r\n\r\n<p>An Lạc thỏa m&atilde;n bạn h&agrave;ng theo 5 mục ti&ecirc;u ch&iacute;nh:</p>\r\n\r\n<p>- Chất lượng ổn định</p>\r\n\r\n<p>- Giao h&agrave;ng đ&uacute;ng hạn</p>\r\n\r\n<p>- Dịch vụ ho&agrave;n thiện</p>\r\n\r\n<p>- Cải tiến thường xuy&ecirc;n</p>\r\n\r\n<p>- Cạnh tranh hiệu quả</p>\r\n\r\n<p><strong>Hệ thống quản l&yacute; chất lượng</strong></p>\r\n\r\n<p>Hệ thống hoạch định nguồn lực doanh nghiệp (ERP &ndash; Enterprise Resource Planning) gi&uacute;p n&acirc;ng cao năng suất tổng thể v&agrave; l&agrave;m đ&ograve;n bẩy quản l&yacute; chất lượng, cụ thể:</p>\r\n\r\n<p>- Sắp xếp chuỗi cung ứng từ thu mua, quy tr&igrave;nh đặt mua h&agrave;ng từ bạn h&agrave;ng, sản xuất v&agrave; tồn kho.</p>\r\n\r\n<p>- Ho&agrave;n th&agrave;nh c&ocirc;ng việc đ&uacute;ng thời hạn với sự gi&aacute;m s&aacute;t tốt hơn.</p>\r\n\r\n<p>- Cải thiện dịch vụ kh&aacute;ch h&agrave;ng khi đưa ra quyết định nhanh ch&oacute;ng bằng b&aacute;o c&aacute;o v&agrave; ph&acirc;n t&iacute;ch dễ sử dụng.</p>\r\n\r\n<p>- Kiểm so&aacute;t được nguồn t&agrave;i nguy&ecirc;n, d&ograve;ng tiền v&agrave; kế to&aacute;n.</p>\r\n\r\n<p><strong>ISO 9001:2008 Hệ thống quản l&yacute; chất lượng</strong></p>\r\n\r\n<p>Thể hiện khả năng của AL c&oacute; thể cung cấp sản phẩm đ&aacute;p ứng nhu cầu của bạn h&agrave;ng, c&aacute;c quy định v&agrave; ph&aacute;p luật hiện h&agrave;nh.</p>\r\n\r\n<p>Gia tăng sự thỏa m&atilde;n của bạn h&agrave;ng th&ocirc;ng qua ứng dụng hệ thống hiệu quả bao gồm c&aacute;c quy tr&igrave;nh nhằm cải tiến li&ecirc;n tục cho hệ thống v&agrave; đảm bảo đ&aacute;p ứng nhu cầu của bạn h&agrave;ng, c&aacute;c quy định ph&aacute;p luật hiện h&agrave;nh.</p>\r\n\r\n<p><strong>ISO 14001:2004 Hệ thống quản l&yacute; m&ocirc;i trường</strong></p>\r\n\r\n<p>Tr&ecirc;n tinh thần &ldquo;CẢI THIỆN V&Agrave; BẢO VỆ M&Ocirc;I TRƯỜNG&rdquo;, ch&uacute;ng t&ocirc;i cam kết x&acirc;y dựng, thực hiện v&agrave; duy tr&igrave; hệ thống quản l&yacute; m&ocirc;i trường ISO 14001:2004 dựa tr&ecirc;n những nguy&ecirc;n tắc cơ bản sau đ&acirc;y:</p>\r\n\r\n<p>- Tu&acirc;n thủ triệt để c&aacute;c qui định ph&aacute;p luật v&agrave; c&aacute;c y&ecirc;u cầu kh&aacute;c li&ecirc;n quan đến c&aacute;c kh&iacute;a cạnh m&ocirc;i trường c&ocirc;ng ty.</p>\r\n\r\n<p>- Lu&ocirc;n xem x&eacute;t cải tiến trong mọi hoạt động, t&iacute;ch cự triển khai c&aacute;c mục ti&ecirc;u v&agrave; chương tr&igrave;nh cải tiến nhằm ho&agrave;n thiện hệ thống m&ocirc;i trường.</p>\r\n\r\n<p>- Bảo vệ m&ocirc;i trường bằng c&aacute;ch kiểm so&aacute;t tốt vấn đề chất thải, t&agrave;i nguy&ecirc;n v&agrave; sự cố m&ocirc;i trường th&ocirc;ng qua thực hiện c&aacute;c biện ph&aacute;p kiểm so&aacute;t điều h&agrave;nh th&iacute;ch hợp v&agrave; gi&aacute;m s&aacute;t c&oacute; hiệu quả.</p>\r\n\r\n<p>- Tăng cường c&ocirc;ng t&aacute;c đ&agrave;o tạo v&agrave; th&ocirc;ng tin m&ocirc;i trường đến nh&acirc;n vi&ecirc;n trong c&ocirc;ng ty v&agrave; c&aacute;c b&ecirc;n li&ecirc;n quan, đảm bảo nguồn lực đ&aacute;p ứng y&ecirc;u cầu của hệ th&ocirc;ng quản l&yacute; m&ocirc;i trường.</p>\r\n\r\n<p>- An Lạc mong muốn g&oacute;p phần x&acirc;y dựng m&ocirc;i trường sống ng&agrave;y c&agrave;ng tốt hơn.</p>\r\n\r\n<p><strong>5s</strong></p>\r\n\r\n<p>Sắp xếp nơi l&agrave;m việc để hạn chế l&atilde;ng ph&iacute; (thời gian, nguồn lực v&agrave; con người) ch&iacute;nh l&agrave; kết quả của nơi l&agrave;m việc được sắp xếp k&eacute;m.</p>\r\n\r\n<p>TPM (Total Productive Mainternance)&nbsp;- Bảo tr&igrave; năng suất to&agrave;n diện</p>\r\n\r\n<p>Bảo tr&igrave; chủ động v&agrave; ph&ograve;ng ngừa để tối đa h&oacute;a thời gian hoạt động của thiết bị.</p>\r\n\r\n<p>Ph&acirc;n t&iacute;ch nguy&ecirc;n nh&acirc;n gốc rễ Đảm bảo vấn đề được thực sự loại bỏ bằng c&aacute;ch giải quyết đ&uacute;ng nguy&ecirc;n nh&acirc;n gốc rễ của vấn đề sau khi trả lời c&acirc;u hỏi tại sao năm lần để kh&aacute;m ph&aacute; c&aacute;c vấn đề tiềm ẩn thật sự.</p>\r\n\r\n<p>KPI (Key Performance Indicator)&nbsp;&ndash; Chỉ số đo lường hiệu suất c&ocirc;ng việc</p>\r\n\r\n<p>C&ocirc;ng việc được thiết kế theo số liệu để theo d&otilde;i v&agrave; khuyến kh&iacute;ch sự tiến bộ của nh&acirc;n vi&ecirc;n hướng tới mục ti&ecirc;u quan trọng của An Lạc.</p>\r\n\r\n<p><strong>CH&Iacute;NH S&Aacute;CH DỊCH VỤ BẠN H&Agrave;NG</strong></p>\r\n\r\n<p><strong>Y&ecirc;u cầu dịch vụ bạn h&agrave;ng:</strong></p>\r\n\r\n<p>Th&ocirc;ng tin giải đ&aacute;p cho bạn h&agrave;ng trong thời gian nhanh nhất nhằm thỏa m&atilde;n y&ecirc;u cầu bạn h&agrave;ng.</p>\r\n\r\n<p>Kế hoạch bảo mật th&ocirc;ng tin của bạn h&agrave;ng đối với những mặt h&agrave;ng lần đầu tung ra thị trường hoặc những chương tr&igrave;nh khuyến m&atilde;i.</p>\r\n\r\n<p><strong>Quản trị rủi ro</strong></p>\r\n\r\n<p>Kế hoạch dự ph&ograve;ng, đảm bảo sản xuất nhằm đề ph&ograve;ng, ứng ph&oacute;, kh&ocirc;i phục hoạt động trước c&aacute;c sự kiện xảy ra ngo&agrave;i dự kiến hoặc bất khả kh&aacute;ng để đảm bảo việc giao h&agrave;ng cho bạn h&agrave;ng.</p>\r\n\r\n<p>Sắp xếp nơi l&agrave;m việc để hạn chế l&atilde;ng ph&iacute; (thời gian, nguồn lực v&agrave; con người) ch&iacute;nh l&agrave; kết quả của nơi l&agrave;m việc được sắp xếp k&eacute;m.</p>\r\n\r\n<p>TPM (Total Productive Mainternance)&nbsp;- Bảo tr&igrave; năng suất to&agrave;n diện</p>\r\n\r\n<p>Bảo tr&igrave; chủ động v&agrave; ph&ograve;ng ngừa để tối đa h&oacute;a thời gian hoạt động của thiết bị.</p>\r\n\r\n<p>Ph&acirc;n t&iacute;ch nguy&ecirc;n nh&acirc;n gốc rễ Đảm bảo vấn đề được thực sự loại bỏ bằng c&aacute;ch giải quyết đ&uacute;ng nguy&ecirc;n nh&acirc;n gốc rễ của vấn đề sau khi trả lời c&acirc;u hỏi tại sao năm lần để kh&aacute;m ph&aacute; c&aacute;c vấn đề tiềm ẩn thật sự.</p>\r\n\r\n<p>KPI (Key Performance Indicator)&nbsp;&ndash; Chỉ số đo lường hiệu suất c&ocirc;ng việc</p>\r\n\r\n<p>C&ocirc;ng việc được thiết kế theo số liệu để theo d&otilde;i v&agrave; khuyến kh&iacute;ch sự tiến bộ của nh&acirc;n vi&ecirc;n hướng tới mục ti&ecirc;u quan trọng của An Lạc.</p>\r\n\r\n<p><strong>CH&Iacute;NH S&Aacute;CH DỊCH VỤ BẠN H&Agrave;NG</strong></p>\r\n\r\n<p><strong>Y&ecirc;u cầu dịch vụ bạn h&agrave;ng:</strong></p>\r\n\r\n<p>Th&ocirc;ng tin giải đ&aacute;p cho bạn h&agrave;ng trong thời gian nhanh nhất nhằm thỏa m&atilde;n y&ecirc;u cầu bạn h&agrave;ng.</p>\r\n\r\n<p>Kế hoạch bảo mật th&ocirc;ng tin của bạn h&agrave;ng đối với những mặt h&agrave;ng lần đầu tung ra thị trường hoặc những chương tr&igrave;nh khuyến m&atilde;i.</p>\r\n\r\n<p><strong>Quản trị rủi ro</strong></p>\r\n\r\n<p>Kế hoạch dự ph&ograve;ng, đảm bảo sản xuất nhằm đề ph&ograve;ng, ứng ph&oacute;, kh&ocirc;i phục hoạt động trước c&aacute;c sự kiện xảy ra ngo&agrave;i dự kiến hoặc bất khả kh&aacute;ng để đảm bảo việc giao h&agrave;ng cho bạn h&agrave;ng.</p>', '1628175054_logo.jpg', 1, 7, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'cl', '2021-08-02 08:35:14', '2021-08-05 14:50:54'),
(3, 'Chọn màu sơn tường nhà phù hợp?', 'Khi lựa chọn màu sơn cho nhà ở, nhiều gia chủ vẫn thường băn khoăn giữa việc nên chọn màu sắc phù hợp với mệnh, theo hướng công trình hay theo hướng khí hậu. Điều đó gây khó khăn cho việc sử dụng sơn màu phù hợp cho ngôi nhà. Tuy nhiên, mỗi cách lựa chọn có một ưu điểm riêng, sẽ giúp không gian sống hài hòa, cân bằng ánh sáng và tạo cảm giác dễ chịu cho các thành viên trong gia đình.', '<ul>\r\n	<li>Chọn m&agrave;u sắc theo mệnh</li>\r\n	<li>Chọn m&agrave;u theo hướng c&ocirc;ng tr&igrave;nh</li>\r\n	<li>Chọn m&agrave;u theo hướng kh&iacute; hậu</li>\r\n</ul>\r\n\r\n<h3>1. C&aacute;ch lựa chọn m&agrave;u sắc sơn nh&agrave; theo mệnh của gia chủ:</h3>\r\n\r\n<ul>\r\n	<li>Mệnh Kim: N&ecirc;n sử dụng sơn m&agrave;u trắng hoặc sơn m&agrave;u v&agrave;ng.</li>\r\n	<li>Mệnh Mộc: Sơn m&agrave;u xanh hoặc m&agrave;u đen.</li>\r\n	<li>Mệnh Thủy: Sơn m&agrave;u đen, xanh lam, m&agrave;u trắng.</li>\r\n	<li>Mệnh Hỏa: Sơn m&agrave;u hồng, m&agrave;u dam cam, m&agrave;u đỏ.</li>\r\n	<li>Mệnh Thổ: Sơn m&agrave;u v&agrave;ng đất, m&agrave;u n&acirc;u, m&agrave;u v&agrave;ng x&aacute;m.</li>\r\n</ul>\r\n\r\n<p>Sở dĩ c&oacute; sự lựa chọn tr&ecirc;n l&agrave; do quy luật ngũ h&agrave;nh: Thổ sinh Kim, Kim -&gt; Thủy, Thủy -&gt; Mộc, Mộc -&gt; Hỏa, Hỏa -&gt; Thổ</p>\r\n\r\n<h3>2. C&aacute;ch chọn m&agrave;u sơn nh&agrave; theo hướng cửa ch&iacute;nh( Hướng của c&ocirc;ng tr&igrave;nh)</h3>\r\n\r\n<ul>\r\n	<li>Hướng Đ&ocirc;ng: sơn m&agrave;u xanh</li>\r\n	<li>Hướng T&acirc;y: sơn m&agrave;u trắng, sữa.</li>\r\n	<li>Hướng Nam: sơn m&agrave;u đỏ, hồng.</li>\r\n	<li>Hướng Bắc: sơn m&agrave;u ghi, x&aacute;m, xanh dương</li>\r\n</ul>\r\n\r\n<p><strong>Nh&agrave; hướng T&acirc;y, T&acirc;y Bắc</strong>&nbsp;đ&oacute;n nhiều &aacute;nh s&aacute;ng n&ecirc;n chọn c&aacute;c gam m&agrave;u lạnh v&agrave; m&agrave;u trung t&iacute;nh như xanh l&aacute;, xanh da trời, trắng sữa&hellip; Nh&agrave; hướng Bắc &iacute;t &aacute;nh s&aacute;ng, d&ugrave;ng m&agrave;u trắng s&aacute;ng gi&uacute;p kh&ocirc;ng gian s&aacute;ng sủa hơn. Nh&agrave; hướng Đ&ocirc;ng Bắc hợp với c&aacute;c gam m&agrave;u n&oacute;ng để l&agrave;m bớt sự lạnh lẽo.</p>\r\n\r\n<p><strong>Nh&agrave; hướng Nam, Đ&ocirc;ng Nam, T&acirc;y Nam</strong>&nbsp;c&oacute; nguồn s&aacute;ng ổn định, ấm &aacute;p n&ecirc;n hợp với bất kỳ m&agrave;u n&agrave;o m&agrave; chủ nh&acirc;n ưa th&iacute;ch. Đối với từng căn ph&ograve;ng, bạn cũng n&ecirc;n c&acirc;n nhắc c&aacute;c m&agrave;u sắc ph&ugrave; hợp. Ph&ograve;ng l&agrave;m việc hay ph&ograve;ng học th&iacute;ch hợp với c&aacute;c m&agrave;u đỏ, cam, v&agrave;ng. Đ&acirc;y l&agrave; những m&agrave;u truyền cảm hứng l&agrave;m việc, thuộc năng lượng dương. Ngược lại, c&aacute;c m&agrave;u thuộc năng lượng &acirc;m như xanh, trắng, hồng&hellip;. mang lại sự y&ecirc;n tĩnh v&agrave; những cảm x&uacute;c y&ecirc;u thương, ph&ugrave; hợp với ph&ograve;ng ngủ.</p>\r\n\r\n<h3><strong>3. C&aacute;ch chọn m&agrave;u sơn nh&agrave; theo kh&iacute; hậu:</strong></h3>\r\n\r\n<ul>\r\n	<li><strong>Nh&agrave; theo hướng ch&iacute;nh T&acirc;y, T&acirc;y Bắc</strong>, thời gian &aacute;nh nắng mặt trời chiếu trực tiếp v&agrave;o nh&agrave; nhiều nhất, ưu điểm l&agrave; tận dụng được tốt nguồn s&aacute;ng tự nhi&ecirc;n, tuy nhi&ecirc;n điểm hạn chế l&agrave; ch&uacute;ng g&acirc;y cảm gi&aacute;c n&oacute;ng nực, kh&oacute; chịu cho người sử dụng, đặc biệt l&agrave; v&agrave;o m&ugrave;a h&egrave;. Bởi vậy nh&agrave; hướng n&agrave;y n&ecirc;n n&ecirc;n chọn m&agrave;u lạnh, trung h&ograve;a như xanh da trời, l&aacute; c&acirc;y, trắng, sữa&hellip;.</li>\r\n	<li><strong>Nh&agrave; hướng Nam, Đ&ocirc;ng Nam v&agrave; T&acirc;y Nam</strong>&nbsp;l&agrave; những hướng tốt bởi c&oacute; nguồn s&aacute;ng ổn định, đ&oacute;n gi&oacute; tốt v&agrave; kh&ocirc;ng kh&iacute; ấm &aacute;p n&ecirc;n c&oacute; thể phối m&agrave;u hợp với từng kh&ocirc;ng gian sử dụng hoặc theo sở th&iacute;ch của chủ nh&acirc;n ng&ocirc;i nh&agrave;.</li>\r\n	<li><strong>M&agrave;u n&oacute;ng cho nh&agrave; hướng Đ&ocirc;ng Bắc:</strong>&nbsp;Nh&agrave; hướng Đ&ocirc;ng Bắc n&ecirc;n sử dụng m&agrave;u n&oacute;ng v&igrave; v&agrave;o m&ugrave;a đ&ocirc;ng sẽ chịu ảnh hưởng của gi&oacute; m&ugrave;a Đ&ocirc;ng Bắc v&agrave;o nh&agrave;, g&acirc;y cảm gi&aacute;c lạnh lẽo, trống trải. Hướng Bắc nhận được &iacute;t &aacute;nh s&aacute;ng n&ecirc;n d&ugrave;ng m&agrave;u s&aacute;ng sẽ gi&uacute;p kh&ocirc;ng gian rộng v&agrave; s&aacute;ng sủa hơn.</li>\r\n	<li><strong>Chọn m&agrave;u sắc cho trần nh&agrave; để hợp phong thuỷ với gia chủ:</strong>&nbsp;Theo quan niệm phong thủy phương Đ&ocirc;ng, trần nh&agrave; m&agrave;u tối lu&ocirc;n g&acirc;y cảm gi&aacute;c nặng nề, bức bối. Hơn nữa, m&agrave;u tối như đ&aacute;m m&acirc;y đen lu&acirc;n chuyển khiến cho tiền t&agrave;i, sức khỏe v&agrave; vận may của gia đ&igrave;nh dễ ti&ecirc;u tan. V&igrave; v&acirc;y, bạn n&ecirc;n sơn trần nh&agrave; c&oacute; t&ocirc;ng m&agrave;u s&aacute;ng hoặc m&agrave;u nhạt, trang nh&atilde;.</li>\r\n</ul>', '1628175186_2f90cfc212ade5f3bcbc.jpg', 1, 2, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 08:36:23', '2021-08-05 15:39:16'),
(4, 'Sơn tường gạch nội thất với năm bước đơn giản', 'Thêm một lớp sơn cho tường gạch là một cách đơn giản làm thay đổi diện mạo của một căn phòng.', '<h2>Nếu bạn y&ecirc;u th&iacute;ch những họa tiết giống vải dệt m&agrave; tường gạch mang lại cho một căn ph&ograve;ng, nhưng c&oacute; cảm gi&aacute;c m&agrave;u sắc kh&ocirc;ng được ph&ugrave; hợp lắm, h&atilde;y thử l&agrave;m mới bức tường của bạn bằng một mảng m&agrave;u. Sau đ&acirc;y l&agrave; c&aacute;ch tạo một bề mặt kh&ocirc;ng t&igrave; vết:</h2>\r\n\r\n<ol>\r\n	<li>Điều quan trọng l&agrave; bắt đầu với một tấm vải dầu trắng. Ch&agrave; kỹ bức tường bằng một miếng giấy nh&aacute;m hoặc một c&aacute;i chổi cứng v&agrave; d&ugrave;ng m&aacute;y h&uacute;t bụi l&agrave;m sạch c&aacute;c miếng vữa vụn hoặc bụi bẩ<img alt=\"\" src=\"http://localhost:8090/project/goldenstar/public/img/1628176072_Augcach-son-tuong-gach-noi-that.jpg\" style=\"float:right; height:225px; margin:10px; width:400px\" />n. Lau sạch bề mặt gạch bằng một miếng vải ẩm nh&uacute;ng x&agrave; ph&ograve;ng v&agrave; để kh&ocirc; trong 12 tiếng.</li>\r\n	<li>Bạn c&oacute; nh&igrave;n thấy c&aacute;c vết nứt tr&ecirc;n gạch hay mạch vữa kh&ocirc;ng? Nếu c&oacute;, h&atilde;y d&ugrave;ng sản phẩm v&agrave; l&agrave;m theo hướng dẫn về thời gian sơn kh&ocirc; in tr&ecirc;n bao b&igrave;.<br />\r\n	&nbsp;</li>\r\n	<li>Tiếp theo, d&ugrave;ng cọ lăn loại sợi vừa hoặc sợi d&agrave;i sơn một lớp l&oacute;t v&agrave; lớp sơn giữa ph&ugrave; hợp với gạch (bề mặt tường c&agrave;ng r&aacute;p, bạn sẽ cần loại cọ sợi c&agrave;ng d&agrave;i). D&ugrave;ng cọ sơn một lớp sơn l&oacute;t b&ecirc;n trong tất cả những điểm nối m&agrave; cọ lăn kh&ocirc;ng lăn tới. Để sơn l&oacute;t thật kh&ocirc; trước khi bắt đầu sơn - xem hướng dẫn về thời gian chờ sơn kh&ocirc; nếu bạn kh&ocirc;ng chắc chắn.<br />\r\n	&nbsp;</li>\r\n	<li>Chọn một loại sơn acrylic gốc nước chuy&ecirc;n d&ugrave;ng cho nội thất, v&agrave; sơn bức tường gạch của bạn giống như sơn mọi bức tường nội thất kh&aacute;c, d&ugrave;ng ch&iacute;nh c&aacute;i cọ lăn m&agrave; bạn đ&atilde; d&ugrave;ng để sơn sơn l&oacute;t. Nếu kh&ocirc;ng gian bạn đang sơn liền với một bức tường hoặc trần nh&agrave; c&oacute; m&agrave;u kh&aacute;c, bạn sẽ cần x&oacute;a g&oacute;c v&agrave; c&aacute;c gờ tường bằng một c&aacute;i chổi để tạo bề mặt tr&ocirc;ng chuy&ecirc;n nghiệp.<br />\r\n	&nbsp;</li>\r\n	<li>Tường gạch trần xốp hơn tường tr&aacute;t vữa th&ocirc;ng thường, do đ&oacute; bạn sẽ cần &iacute;t nhất hai lớp sơn để mặt tường tr&ocirc;ng đồng nhất. Để từng lớp sơn kh&ocirc; ho&agrave;n to&agrave;n trước khi sơn một lớp mới - kh&ocirc;ng n&ecirc;n sơn qu&aacute; bốn nước.</li>\r\n</ol>', '1628176102_Augcach-son-tuong-gach-noi-that.jpg', 1, 8, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 08:57:03', '2021-08-05 15:12:22'),
(5, 'Chuyên gia màu sắc tư vấn 3 phong cách thiết kế căn hộ năm 2019', 'Bà Lisbeth Larsen - Giám đốc màu sắc Jotun toàn cầu, giới thiệu xu hướng thiết kế 2019 tập trung vào 3 phong cách: nhẹ nhàng, tinh tế, thô mộc.', '<p>B&agrave; Lisbeth Larsen - Gi&aacute;m đốc m&agrave;u sắc Jotun to&agrave;n cầu, giới thiệu xu hướng thiết kế 2019 tập trung v&agrave;o 3 phong c&aacute;ch: nhẹ nh&agrave;ng, tinh tế, th&ocirc; mộc.</p>\r\n\r\n<p>Trong sự kiện c&ocirc;ng bố bộ sưu tập m&agrave;u sắc 2019 vừa diễn ra ở TP HCM, b&agrave;&nbsp;Lisbeth Larsen chia sẻ:&nbsp;&quot;Ch&uacute;ng t&ocirc;i tin rằng m&agrave;u sắc l&agrave; c&aacute;ch pha trộn tự nhi&ecirc;n của cuộc đời để truyền cảm hứng cho ch&uacute;ng ta ho&agrave;n thiện v&agrave; thưởng thức cuộc sống&quot;.&nbsp;</p>\r\n\r\n<p>V&igrave; vậy trong bộ sưu tập cho năm sau, Jotun chọn con người l&agrave; yếu tố trung t&acirc;m để nghi&ecirc;n cứu về nh&oacute;m m&agrave;u ph&ugrave; hợp với xu hướng hiện đại, mang lại những cảm x&uacute;c cho chủ nh&acirc;n căn hộ. C&aacute;c nh&agrave; thiết kế truyền đi th&ocirc;n<img alt=\"Bà Lisbeth Larsen - Giám đốc màu sắc Jotun toàn cầu.\" src=\"https://i1-giadinh.vnecdn.net/2018/11/27/01-2944-1543297790.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kTHLLm_bU1yfdOvkZBFsag\" style=\"float:right; height:406px; width:350px\" />g điệp, ng&ocirc;i nh&agrave; l&agrave; sự phản &aacute;nh của gia chủ, thể hiện c&aacute; t&iacute;nh thay v&igrave; đi theo xu hướng cố định. V&igrave; vậy, m&agrave;u sắc v&agrave; vật dụng trang tr&iacute; nh&agrave; được gợi &yacute; dựa tr&ecirc;n 3 c&aacute; t&iacute;nh:&nbsp;nhẹ nh&agrave;ng, tinh tế, th&ocirc; mộc.</p>\r\n\r\n<p>B&agrave; Lisbeth Larsen - Gi&aacute;m đốc m&agrave;u sắc Jotun to&agrave;n cầu.</p>\r\n\r\n<p>Thiết kế nhẹ nh&agrave;ng</p>\r\n\r\n<p>Nhẹ nh&agrave;ng được Jotun định nghĩa l&agrave; vẻ đẹp của sự đơn giản, gi&uacute;p t&acirc;m tr&iacute; gia chủ ho&agrave;n to&agrave;n thư th&aacute;i v&agrave; tĩnh lặng mỗi khi trở về tổ ấm. Phong c&aacute;ch n&agrave;y đề cao sự đơn giản, t&ocirc;ng chủ đạo l&agrave; m&agrave;u trung t&iacute;nh.</p>\r\n\r\n<p>Thiết kế được sử dụng kh&aacute; đơn giản, c&oacute; hơi hướm l&agrave;m nổi bật t&ocirc;ng m&agrave;u lạnh v&agrave; ưu ti&ecirc;n nội thất Bắc &Acirc;u hay Thiền định (Zen). Vật gia dụng lẫn b&agrave;n, ghế đều m&agrave;u trơn, kh&ocirc;ng họa tiết. C&aacute;c đường n&eacute;t của mỗi m&oacute;n đồ cũng được&nbsp;tối giản,&nbsp;thanh tho&aacute;t, hạn chế hoa văn rườm r&agrave;.</p>\r\n\r\n<p>Xu hướng nhẹ nh&agrave;ng ưu ti&ecirc;n gam m&agrave;u lạnh, hạn chế rườm r&agrave;.</p>\r\n\r\n<p><strong>Xu hướng tinh tế</strong></p>\r\n\r\n<p>Ở lối thiết kế thứ 2, b&agrave; Lisbeth Larsen cho biết ng&ocirc;i nh&agrave; cũng giống như một bảo t&agrave;ng nghệ thuật - nơi lưu trữ, trưng b&agrave;y những kỷ niệm v&agrave; hy vọng c&ugrave;ng những vật phẩm m&agrave; chủ nh&acirc;n đ&atilde; thu thập trong suốt cuộc h&agrave;nh tr&igrave;nh của m&igrave;nh.&nbsp;</p>\r\n\r\n<p>V&igrave; vậy, vị chuy&ecirc;n gia tư vấn nếu muốn đề cao sự s&aacute;ng tạo v&agrave; c&aacute; t&iacute;nh, bạn h&atilde;y ph&aacute; vỡ c&aacute;c lối m&ograve;n, tư duy truyền thống. Điều n&agrave;y đạt được qua sự kết hợp m&agrave;u sắc để tạo hiệu ứng nổi bật, lợi dụng sự tương phản để tăng t&iacute;nh ấn tượng cho kh&ocirc;ng gian.</p>\r\n\r\n<p>Khi tư duy ng&ocirc;i nh&agrave; l&agrave; một khung h&igrave;nh, bạn c&oacute; thể c&oacute; những thử nghiệm t&aacute;o bạo như d&ugrave;ng bộ b&agrave;n ăn đen tuyền từ inox, đường n&eacute;t mảnh đặt trong gian ph&ograve;ng c&oacute; tường sơn v&agrave;ng pastel. C&aacute;ch phối hợp ghế ngồi, đồng hồ v&agrave; cả r&egrave;m cửa cũng đầy ngẫu hứng, mang đến cảm hứng tươi mới mỗi ng&agrave;y.</p>\r\n\r\n<p><img alt=\"BỘ SƯU TẬP MÀU SẮC TRONG KIẾN TRÚC: TRỞ VỀ VỚI TỰ NHIÊN! - Xin bài edit, Nguyên làm giúp chị  - 1\" src=\"https://i1-giadinh.vnecdn.net/2018/11/26/26-11-201846-496461830-w500-2392-1543228985.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7qCBDLexzBGoQU_gPilX-Q\" style=\"height:300px; width:300px\" /></p>\r\n\r\n<p>Gian ph&ograve;ng tinh tế thể hiện qua c&aacute;ch phối hợp m&agrave;u sắc, nội thất đầy s&aacute;ng tạo m&agrave; cũng t&agrave;i t&igrave;nh.</p>\r\n\r\n<p>Cảm hứng th&ocirc; mộc</p>\r\n\r\n<p>Sự th&ocirc; mộc của thi&ecirc;n nhi&ecirc;n l&agrave; nguồn cảm x&uacute;c gi&uacute;p c&acirc;n bằng kh&ocirc;ng gian hiện đại. Lối thiết kế th&ocirc; mộc của Jotun d&ugrave;ng t&ocirc;ng m&agrave;u ấm &aacute;p tự nhi&ecirc;n của đất v&agrave; c&aacute;t để tạo n&ecirc;n phong c&aacute;ch mạnh mẽ nhưng kh&ocirc;ng qu&aacute; nhiều &aacute;p lực, đảm bảo t&iacute;nh nhẹ nh&agrave;ng.&nbsp;</p>\r\n\r\n<p>Gợi nhắc về nguồn cội l&agrave;ng qu&ecirc;,&nbsp;ng&ocirc;i nh&agrave; th&ocirc; mộc c&oacute; thiết kế m&agrave;u sắc v&agrave; chất liệu thủ c&ocirc;ng lột tả ho&agrave;n to&agrave;n sự ch&acirc;n thực, vẻ đẹp kh&ocirc;ng ho&agrave;n hảo của tự nhi&ecirc;n khi chưa qua c&aacute;c c&ocirc;ng đoạn xử l&yacute;. Phong c&aacute;ch n&agrave;y tạo n&ecirc;n sợi d&acirc;y kết nối ng&ocirc;i nh&agrave; với m&ocirc;i trường xung quanh, phần &aacute;p m&aacute;i v&agrave; cột chống l&agrave;m từ gỗ, kh&ocirc;ng được m&agrave;i phẳng m&agrave; vẫn gồ ghề, th&ocirc; r&aacute;p.</p>\r\n\r\n<p>Để h&agrave;i h&ograve;a với c&aacute;c m&oacute;n đồ th&ocirc;, mộc, chuy&ecirc;n gia Jotun tư vấn n&ecirc;n chọn sơn tường cũng thi&ecirc;n về m&agrave;u gỗ, đất. Tr&aacute;nh m&agrave;u đậm v&agrave; n&ecirc;n ưu ti&ecirc;n tường hơi ngả sang gam pastel để kh&ocirc;ng gian sống nhẹ nh&agrave;ng, thư th&aacute;i, tĩnh lặng hơn.</p>\r\n\r\n<p><img alt=\"Phòng cách thiết kế theo chủ đề Thô mộc.\" src=\"https://i1-giadinh.vnecdn.net/2018/11/27/image003-8561-1543297790.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YbIWvMELBITctBgSVESIBQ\" style=\"height:300px; width:300px\" /></p>\r\n\r\n<p>Ph&ograve;ng c&aacute;ch thiết kế theo chủ đề &quot;Th&ocirc; mộc&quot;.</p>\r\n\r\n<p>Ngo&agrave;i những gợi &yacute; ph&iacute;a tr&ecirc;n, nhiều năm qua Jotun cũng đ&atilde; đầu tư nghi&ecirc;n cứu m&agrave;u sắc, ch&uacute; trọng kh&aacute;i niệm c&aacute; nh&acirc;n h&oacute;a để gi&uacute;p con người cảm thụ được những m&agrave;u sắc thời thượng, c&aacute; t&iacute;nh hơn.&nbsp;C&aacute;c bộ sưu tập m&agrave;u sắc gi&uacute;p &iacute;ch cho gia chủ v&agrave; kiến tr&uacute;c sư, tăng th&ecirc;m cảm hứng lựa chọn phong c&aacute;ch cho tổ ấm.</p>\r\n\r\n<p>Hơn một năm trước, h&atilde;ng sơn đ&atilde; giới thiệu bộ sưu tập m&agrave;u sắc 2017 gồm 3 gợi &yacute;: Ng&ocirc;i nh&agrave; Nordic với m&agrave;u sắc du mục gần gũi, h&agrave;i ho&agrave; với thi&ecirc;n nhi&ecirc;n; Phong c&aacute;ch đ&ocirc; thị hiện đại, linh hoạt v&agrave; độc đ&aacute;o; Kh&ocirc;ng gian văn ho&aacute; đa sắc th&aacute;i, sinh động v&agrave; ấm c&uacute;ng.</p>\r\n\r\n<p>Đến năm 2018, c&ocirc;ng ch&uacute;ng lại được tiếp x&uacute;c với: Th&agrave;nh phố chuyển động - gam trầm b&igrave;nh dị, s&acirc;u lắng của rừng nhiệt đới; Tổ ấm b&igrave;nh y&ecirc;n - m&agrave;u của sa mạc, hoa đ&agrave;o v&agrave; n&acirc;u c&aacute;t đem lại sự thư gi&atilde;n, y&ecirc;n b&igrave;nh v&agrave; ấm c&uacute;ng; Khu vườn xanh m&aacute;t - đem đến cảm gi&aacute;c thanh b&igrave;nh, tĩnh lặng với sắc m&agrave;u thi&ecirc;n nhi&ecirc;n như &aacute;nh nắng mặt trời, c&acirc;y cối...</p>', '1627894899_image003-8561-1543297790.jpg', 1, 4, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:01:39', '2021-08-05 15:16:22'),
(6, 'Nhà vừa xây xong tường đã nứt chân chim, phải xử lý ra sao?', 'Nhà tôi mới xây xong, trên tường hiện đã xuất hiện vài vết rạn sơn, có cách nào xử lý mà không phải đục tường hay không?', '<p>Vết rạn ch&acirc;n chim như thế c&oacute; phải nh&agrave; n&agrave;o cũng bị hay kh&ocirc;ng, nguy&ecirc;n nh&acirc;n l&agrave; g&igrave;?&nbsp;<em>(Thuận)</em></p>\r\n\r\n<p>Trả lời</p>\r\n\r\n<p>Trường hợp n&agrave;y rất thường hay gặp, do nhiều nguy&ecirc;n nh&acirc;n. Do yếu tố về kh&iacute; hậu, do ch&uacute;ng ta t&ocirc; tr&aacute;t vữa, kh&aacute;ng kiềm, bả mat&iacute;t v&agrave; lăn sơn kh&ocirc;ng đ&uacute;ng quy tr&igrave;nh, kỹ thuật th&igrave; thường một th&aacute;ng sau khi l&agrave;m nh&agrave; xong, bề mặt tường c&oacute; dấu hiệu rạn ch&acirc;n chim. Nếu như vết rạn nhỏ, kh&ocirc;ng k&eacute;o d&agrave;i, kh&ocirc;ng nứt v&agrave;o b&ecirc;n trong cấu tạo của tường th&igrave; bạn kh&ocirc;ng cần phải lo lắng, đ&acirc;y l&agrave; một hiện tượng b&igrave;nh thường, chỉ l&agrave; bề mặt th&ocirc;i.</p>\r\n\r\n<p>Bộ X&acirc;y dựng c&oacute; quy định c&aacute;c nh&agrave; thầu phải bảo h&agrave;nh trong 24 th&aacute;ng. Trong thời gian đ&oacute;, nh&agrave; thầu sẽ chọn một thời điểm th&iacute;ch hợp đến để khắc phục những dấu hiệu nứt rạn ch&acirc;n. Th&ocirc;ng thường c&aacute;c nh&agrave; thầu sẽ để tường rạn hết một năm đầu, năm sau tới bả matit lại, sơn nước lại, sẽ hết.</p>\r\n\r\n<p>Hiện nay, tr&ecirc;n thị trường c&oacute; nhiều loại sơn phủ bề mặt cho ngoại thất v&agrave; nội thất m&agrave; hệ số co gi&atilde;n của sơn l&ecirc;n đến 300%. Tức l&agrave; nếu ch&uacute;ng ta sơn mảng sơn khoảng 10cm, khi kh&ocirc; rồi, ta c&oacute; thể k&eacute;o gi&atilde;n mảng sơn đến 30cm m&agrave; kh&ocirc;ng xảy ra hiện tượng chảy, nứt. Loại sơn n&agrave;y cho ph&eacute;p tường nh&agrave;, đặc biệt bề mặt ngo&agrave;i trời c&oacute; thể chịu được sự thay đổi khắc nghiệt của kh&iacute; hậu, m&agrave; kh&ocirc;ng g&acirc;y rạn nứt bề mặt. Đương nhi&ecirc;n, gi&aacute; th&agrave;nh của những loại sơn n&agrave;y sẽ tương đối đắt, b&ugrave; lại rất bền v&agrave; ch&uacute;ng ta kh&ocirc;ng phải bảo h&agrave;nh bảo dưỡng li&ecirc;n tục ngoại thất ng&ocirc;i nh&agrave;.</p>\r\n\r\n<p>Mọi người đều biết, mỗi lần sơn lại nh&agrave; rất vất vả, đặc biệt sơn trong nội thất. Ch&uacute;ng ta muốn sơn được phải dọn hết đồ đạc ra, phải bả matit. Sau khi l&agrave;m xong, bụi bay mịt m&ugrave;, v&agrave; cũng phải chờ một thời gian nhất định m&ugrave;i sơn mới bay hết. N&ecirc;n chọn một loại sơn tốt, lau ch&ugrave;i được.</p>\r\n\r\n<p>Để hạn chế hiện tượng nứt tường như tr&ecirc;n, chủ đầu tư n&ecirc;n đặt vấn đề với nh&agrave; thầu n&ecirc;n thực hiện đ&uacute;ng quy tr&igrave;nh thi c&ocirc;ng của c&aacute;c h&atilde;ng sơn. Mua đ&uacute;ng loại sơn cho từng vị tr&iacute; tường ph&ugrave; hợp, đừng tiết kiệm kh&ocirc;ng đ&uacute;ng c&aacute;ch đ&ocirc;i khi bạn lại phải trả tiền nhiều hơn thế nữa!</p>', 'default.jpg', 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:02:23', '2021-08-03 12:48:03'),
(7, 'Màu sơn giúp bạn bán nhà vừa nhanh vừa giá cao', 'Nếu không thể sửa sang nhiều, bạn hãy quét màu sơn trắng ngà cho những mảng tường lớn trong nhà.', '<p>Hầu hết mọi người đều biết rằng, khi chuẩn bị b&aacute;n nh&agrave;, bạn n&ecirc;n đầu tư c&ocirc;ng sức để nh&agrave; cửa được ngăn nắp, gọn g&agrave;ng v&agrave; tr&ocirc;ng c&oacute; sức sống hơn. Điều n&agrave;y đồng nghĩa với việc bạn sẽ phải chi một &iacute;t tiền để trang tr&iacute; lại nội ngoại thất. Tuy nhi&ecirc;n, sau đ&oacute;, bạn sẽ dễ d&agrave;ng tăng được gi&aacute; b&aacute;n nh&agrave;.</p>\r\n\r\n<p><img alt=\"mau-son-giup-ban-ban-nha-vua-nhanh-vua-gia-cao\" src=\"https://i1-giadinh.vnecdn.net/2017/11/25/mau-sang-7261-1511578461.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ADzcAVEgxwvaLW6Sh_Pmlg\" /></p>\r\n\r\n<p>Những căn nh&agrave; c&oacute; m&agrave;u trung t&iacute;nh&nbsp;gi&uacute;p người chủ tương lai dễ d&agrave;ng bố tr&iacute; đồ đạc. Ảnh minh họa:&nbsp;<em>Infotel.</em></p>\r\n\r\n<p>Theo b&agrave; Victoria Gimson, Gi&aacute;m đốc thiết kế của c&ocirc;ng ty nội thất Decorum (Australia), c&oacute; nhiều mẹo khi b&aacute;n nh&agrave;. Nhưng nếu bạn kh&ocirc;ng c&oacute; nhiều thời gian v&agrave; tiền bạc, chỉ cần qu&eacute;t tường sang một m&agrave;u sơn mới c&oacute; thể đem lại hiệu quả lớn. M&agrave;u bạn cần chọn l&agrave; m&agrave;u trắng ng&agrave;.</p>\r\n\r\n<p>M&agrave;u trắng ng&agrave; mang t&iacute;nh chất cổ điển, hợp với mọi thời đại, kh&ocirc;ng bị lỗi mốt. B&agrave; Gimson tin rằng, những bức tường m&agrave;u ng&agrave; khiến ng&ocirc;i nh&agrave; tr&ocirc;ng c&oacute; vẻ đắt tiền hơn. Ngo&agrave;i ra, những bức tường trắng sẽ cung cấp những mảng nền sạch cho c&aacute;c kh&aacute;ch h&agrave;ng tiềm năng bố tr&iacute; đồ đạc sau n&agrave;y.&nbsp;</p>\r\n\r\n<p>Sau khi mua, người chủ mới c&oacute; thể dễ d&agrave;ng trang tr&iacute; nh&agrave; cửa. Hầu hết c&aacute;c m&oacute;n đồ nội thất d&ugrave; c&oacute; m&agrave;u sắc thế n&agrave;o cũng dễ d&agrave;ng h&agrave;i h&ograve;a tr&ecirc;n nền m&agrave;u trắng ng&agrave;. Chủ nh&agrave; kh&ocirc;ng phải t&iacute;nh to&aacute;n việc sơn lại ngay lập tức những mảng m&agrave;u qu&aacute; nổi bật hoặc đối chọi với đồ d&ugrave;ng hiện c&oacute;.</p>\r\n\r\n<p>Bởi vậy, theo&nbsp;<em>House Beautiful</em>, kh&aacute;ch h&agrave;ng sẽ cho rằng m&igrave;nh kh&ocirc;ng phải tốn k&eacute;m nhiều tiền cho việc cải tạo nh&agrave; mới mua. Do đ&oacute;, việc giao dịch nh&agrave; cửa thuận lợi hơn với mức gi&aacute; cao hơn h&agrave;ng ngh&igrave;n USD (t&ugrave;y diện t&iacute;ch nh&agrave;).</p>\r\n\r\n<p>Chuy&ecirc;n gia nội thất cũng khuy&ecirc;n, người b&aacute;n nh&agrave; đừng vội k&ecirc; hết đồ đạc đi. H&atilde;y để lại những nội thất cơ bản, sử dụng những m&oacute;n đồ c&oacute; m&agrave;u sắc tươi vui, c&acirc;y trồng trang tr&iacute; gi&uacute;p cho ng&ocirc;i nh&agrave; c&oacute; cảm gi&aacute;c ấm c&uacute;ng.</p>', '1627895043_mau-sang-7261-1511578461.jpg', 0, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ps', '2021-08-02 09:04:03', '2021-08-03 14:20:05'),
(9, 'TỔ CHỨC HOẠT ĐỘNG NGOẠI KHÓA', NULL, '<h1><a href=\"https://spr.vn/to-chuc-hoat-dong-ngoai-khoa-cho-doanh-nghiep-thi-nen-lam-gi/\" rel=\"bookmark\">TỔ CHỨC HOẠT ĐỘNG NGOẠI KH&Oacute;A CHO DOANH NGHIỆP TH&Igrave; N&Ecirc;N L&Agrave;M G&Igrave;?</a></h1>\r\n\r\n<ul>\r\n	<li>15 Apr, 2021</li>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n	<li><a href=\"https://spr.vn/tt/tin-tuc/\" rel=\"category tag\">Tin tức</a></li>\r\n</ul>\r\n\r\n<p>Nh&igrave;n v&agrave;o những c&ocirc;ng ty, doanh nghiệp lớn đang tồn tại trong nền kinh tế hiện nay th&igrave; c&oacute; thể dễ d&agrave;ng nhận ra được rằng họ đều đầu tư rất mạnh tay v&agrave;o việc chăm lo gi&aacute; trị tinh thần của tập thể nh&acirc;n vi&ecirc;n. Trong đ&oacute; nổi l&ecirc;n l&agrave; việc x&acirc;y dựng, thiết kế những bộ đồng phục đẹp mắt&nbsp;mắt, đầu tư v&agrave;o cơ sở vật chất v&agrave; tổ chức c&aacute;c hoạt động ngoại kho&aacute; một c&aacute;ch thường xuy&ecirc;n, quy m&ocirc;, chất lượng.</p>\r\n\r\n<p><strong>Hoạt động ngoại kho&aacute;</strong>&nbsp;c&oacute; thể cắt nghĩa một c&aacute;ch đơn giản l&agrave; những hoạt động được tổ chức trong một tập thể. Thế nhưng n&oacute; lại nằm ngo&agrave;i phạm vi chủ thể ch&iacute;nh thống. Nh&igrave;n s&acirc;u v&agrave;o tr&ugrave;m kh&aacute;i niệm n&agrave;y th&igrave; c&oacute; thể thấy n&oacute; bao h&agrave;m c&aacute;c hoạt động văn ho&aacute;, thể thao, giải tr&iacute; được tổ chức trong v&agrave; ngo&agrave;i phạm vi tập thể. Đ&acirc;y được coi l&agrave; một m&ocirc;i trường m&agrave; sự tham gia được dựa tr&ecirc;n sự tự nguyện, nhu cầu v&agrave; khả năng bản th&acirc;n.</p>\r\n\r\n<p>Vai tr&ograve; nổi bật nhất của&nbsp;hoạt động ngoại kho&aacute;&nbsp;l&agrave; tạo ra m&ocirc;i trường để x&acirc;y dựng v&agrave; ph&aacute;t triển quan hệ, mối kết nối giữa c&aacute;c c&aacute;c nh&acirc;n trong tập thể v&agrave; giữa tập thể với những c&aacute; thể, tổ chức, cơ sở b&ecirc;n ngo&agrave;i hay với to&agrave;n x&atilde; hội. Ở quy m&ocirc; c&ocirc;ng ty, doanh nghiệp th&igrave; những hoạt động ngoại kho&aacute; sẽ c&oacute; &yacute; nghĩa l&agrave; tạo ra khoảng nghỉ cần thiết để lấy lại sự c&acirc;n băng hay duy tr&igrave; nhiệt huyết c&ocirc;ng việc.</p>\r\n\r\n<p>&nbsp;</p>', NULL, 0, 19, NULL, NULL, NULL, NULL, 'Long Xuyên-An Giang', '2021-08-14', NULL, NULL, 'ac', '2021-08-02 09:15:24', '2021-08-03 13:55:47'),
